// =============================================================================
// Stencil Checkout  - Customize the checkout experience
// =============================================================================
//
// This file is for checkout customizations for Default one-page checkout.
//
// -----------------------------------------------------------------------------
@import url('https://fonts.googleapis.com/css?family=Poppins:400,400i,500,500i,700,700i|Poppins:400,500,600,700|Oswald:400,500,600,700|Open+Sans:400,400i,600,700,700i|Source+Sans+Pro:400,400i');
body {
    background: #f5f5f5;
}
body, .HorizontalFormContainer dd .selector span, .fastCartTop div, .list-icon li {
    font-family: Poppins,sans-serif;
    color: #000;
}
.Content h2, .Content h3{
    font-family: Poppins,sans-serif;
    color: #000;
    margin-top: 30px;
    font-weight: 600;
}
.btn {
    margin-top: 15px;
    margin-bottom: 0;
    border: 2px solid #000;
    border-radius: 0;
    padding: 6px 24px!important;
    min-width: 160px;
    height: 40px;
    background-color: #000 !important;
    color: #fff;
    font-family: Poppins,Arial,Helvetica,sans-serif;
    font-size: 13px;
    letter-spacing: .08em;
    font-weight: 700 !important;
    transition: all .3s ease 0s;
    text-transform: uppercase;
    vertical-align: middle;
    border-radius: 0 !important;
    &:hover {
        background-color: #fff !important;
        border: 2px solid #000 !important;
        color: #000;
    }
}
.page {
    
    & #Header>.inner {
        max-width: 1170px;
    }
    & .main>.inner {
        max-width: 1170px;
    }
    & .footer {
        background: #f5f5f5;
        padding-top: 10px;
        padding-bottom: 74px;
        margin-top: 100px;
        text-align: center;
    }
    & .footer-links>.inner {
        max-width: 1170px;
        & p {
            font-family: 'Poppins', Arial, Helvetica, sans-serif;
            color: #464646;
            letter-spacing: 0.02em;
        }
    }
}

p {
    font-family: 'Poppins', sans-serif;
    font-size: 12px;
    color: #2d2d2d;
}

#Header {
    position: relative;
    background-image: url(/s-wek9ye9/product_images/uploaded_images/header-checkout.jpg);
}

#Header .header-logo {

    img {
        width: auto;
        max-width: 100%;
        display: none;
    }
    a  {
        position: relative;
        display: inline-block;
        width: 280px;
        height: 30px;
        &:before {
            width: 280px;
            height: 30px;
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            background-image: url(/s-wek9ye9/product_images/uploaded_images/logo-checkout.png);
        }
    }
}

.TopMenu>.inner,
.PageMenu>.inner,
.main>.inner,
.footer-main>.inner,
.newsletter-subscription>.inner {
    @media (max-width: 767px) {
        width: 95% !important;
    }
}

.header {
    background: #fff;
}

.header-logo {
    padding: 78px 0 78px;
}

h1.TitleHeading {
    margin: 30px 0 25px;
    font-family: "Poppins", Arial, Helvetica, sans-serif;
    font-size: 20px;
    letter-spacing: .05em;
    text-transform: uppercase;
    display: inline-block;
    line-height: 1;
    color: #000;
    font-weight: 700;
}

.Content .Block.ExpressCheckout {
    border-color: #eeeeee;
    padding-top: 80px;
}
textarea{
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
}
input.Textbox,
 input[type="text"],
    input[type="password"],
    input[type="email"] {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    background-color: #fff;
    border-color: #d9d9d9;
    border-style: solid;
    border-width: 1px;
    border-radius: 4px;
    box-shadow: inset 0 0px 0px transparent;
    color: #333;
    display: block;
    font-family: inherit;
    font-size: 1rem;
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;
    font-smoothing: antialiased;
    height: 3.45rem;
    margin: 0;
    padding: .75rem 1rem;
    transition: all 100ms ease-out;
    width: 100%;
}
.ExpressCheckoutBlock {
    margin-bottom: 15px;  
    padding: 0;
    color: #2d2d2d;
    font-family: 'Poppins', sans-serif;
    font-size: 12px;
    line-height: 0;
    border-bottom: 0;
    letter-spacing: 0.05em;
    h2 {
        padding-bottom: 25px;
        font-family: 'Poppins', sans-serif;
        font-size: 20px;
        font-weight: 700;
        letter-spacing: .05em;
    }
    dt {
        font-family: 'Poppins', sans-serif;
        font-size: 12px;
        font-weight: 700;
    }
    dd>label {
        font-weight: 400;
        letter-spacing: 0.02em;
    }
    a {
        color: #000 !important;
        font-size: 12px;
    }
    p {
        font-size: 12px;
        margin-bottom: 25px;
        letter-spacing: 0.05em;
        @media (max-width: 480px) {
            margin-bottom: 16px;
        }
    }
    &>.ExpressCheckoutContent {
        padding: 34px 0;
        line-height: 1;
        & h4 {
            padding: 3px 0 22px;
            color: #000000;
            font-size: 16px;
            font-family: 'Poppins', sans-serif;
            font-weight: 600;
            letter-spacing: 0.02em;
            text-transform: none;
        }
        @media (max-width: 767px) {
            padding: 20px 20px 0;
        }
        @media screen and (max-width: 1024px) {
            margin-bottom: 10px;
        }
    }
    .mbm,
    .mvm,
    .mam {
        margin-bottom: 18px !important;
    }
    input[type="button"],
    input[type="submit"] {
        margin-top: 0px;
        border: 2px solid #000000;
        border-radius: 0;
        padding: 6px 24px !important;
        min-width: 160px;
        height: 40px;
        background: #000000;
        color: #fff;
        font-family: "Poppins", Arial, Helvetica, sans-serif;
        font-size: 13px;
        letter-spacing: 0.08em;
        font-weight: 600;
        -moz-transition: all 0.3s ease 0s;
        -webkit-transition: all 0.3s ease 0s;
        -ms-transition: all 0.3s ease 0s;
        -o-transition: all 0.3s ease 0s;
        transition: all 0.3s ease 0s;
        margin-bottom: 20px;
        &:hover {
            background-color: #fff;
            border-color: #000;
            color: #000;
        }
    }
    input#bottom_payment_button {
        background: #000000;
        color: #fff;
        border: 2px solid #000000;
        &:hover {
            background: #fff;
            color: #000;
            border: 2px solid #000;
        }
    }
    input[type="text"],
    input[type="password"],
    input[type="email"] {
        border: 1px solid #cbcbcb !important;
        border-radius: 0;
        padding-top: 0;
        padding-bottom: 0;
        box-shadow: none !important;
        background-image: none !important;
        height: 40px !important;
        line-height: 36px !important;
        font-family: 'Poppins', sans-serif;
        font-size: 12px !important;
        color: #414141;
        &:focus {
            border: 1px solid #989898 !important;
        }
    }
    .selector {
        font-family: 'Poppins', sans-serif;
        font-size: 12px!important;
        height: 40px;
        min-height: 40px!important;
        border-radius: 0;
        border-color: #cbcbcb;
        >span {
            font-size: 12px !important;
            line-height: 36px;
            font-family: 'Poppins', sans-serif !important;
            color: #414141;
        }
    }
    select.FormField {
        border-radius: 0;
    }
}

.ExpressCheckoutBlock .ExpressCheckoutTitle {
    display: inline-block;
    position: relative;
    width: 100%;
    margin: 0;
    padding: 0;
    border: 0;
    border-bottom: 2px solid #000;
    &>span.ExpressCheckoutCompletedContent {
        display: inline-block;
        height: 40px;
        float: left;
        font-size: 12px;
        font-family: 'Poppins', sans-serif;
        color: #9e9e9e;
        font-weight: 500;
        line-height: 40px;
        opacity: 0.7;
        @media (max-width: 767px) {
            display: none;
        }
    }
}

.ExpressCheckoutContent .HorizontalFormContainer dd,
#AddressEditForm dd {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    width: 240px;
    @media (max-width: 1024px) {
        width: 320px;
        float: left;
    }
    @media (max-width: 600px) {
        width: 100%;
        float: none;
    }
}

.ExpressCheckoutBlockCompleted a.ChangeLink {
    display: inline-block;
    position: absolute!important;
    top: 11px!important;
    right: 0!important;
    padding: 4px 0px 3px;
    font-family: 'Poppins', sans-serif;
    font-size: 11px;
    font-weight: 700;
    letter-spacing: .08em;
    line-height: 1;
    text-transform: uppercase;
    z-index: 1111;
    color: #1e1e1e;
    border-radius: 0px;
    background: #fff;
    max-width: 48px;
    border: 0;
    &:hover {
        color: #ae895d;
    }
}

.ExpressCheckoutBlock .ExpressCheckoutTitle h3 {
    display: inline-block;
    margin: 0;
    border: 0;
    padding: 0;
    width: 430px;
    float: left;
    font-family: "Poppins", Arial, Helvetica, sans-serif;
    font-size: 18px;
    font-weight: 600;
    letter-spacing: 0.08em;
    line-height: 50px;
    text-transform: capitalize;
    @media (max-width: 991px) {
        width: 400px;
    }
    @media (max-width: 767px) {
        width: 100%;
    }
    & .step {
        display: inline-block;
        position: relative;
        left: 0;
        -webkit-box-sizing: border-box;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        margin-right: 25px;
        border-right: 1px solid #e5e5e5;
        border-radius: 0;
        padding: 0 18px;
        width: 52px;
        height: 50px;
        float: left;
        background: #000;
        color: #fff;
        font-family: "Poppins", Arial, Helvetica, sans-serif;
        font-weight: 600;
        font-size: 18px;
        line-height: 50px !important;
        letter-spacing: .05em;
        text-align: center;
        
    }
}

.ExpressCheckoutBlock .mbm dd {
    display: block;
    margin-right: 30px;
}

.ExpressCheckoutBlock dd>label {
    font-weight: 500;
}

.ExpressCheckoutBlockCollapsed .ExpressCheckoutTitle {
   
}

