    border-radius: 0;
    border-color: #eee;
}

ul.tabs {
    top: 1px;
    li {
        display: inline-block;
        position: relative;
        margin-right: 0;
        border-radius: 0;
        border-right: 0;
        padding: 13px 30px;
        border-bottom: 1px solid #eee;
        background: #fff;
        &:last-child {
            border-right: 1px solid #eee;
        }
    }
}

ul.tabs li.active {
    border-bottom: 1px solid #eee;
}

ul.tabs li.active::before {
    content: "";
    display: inline-block;
    position: absolute;
    top: 0;
    right: 0;
    width: 100%;
    height: 3px;
    background: #000000;
}

ul.tabs li.active::after {
    content: "";
    display: inline-block;
    position: absolute;
    bottom: -1px;
    right: 0;
    width: 100%;
    height: 2px;
    background: #fff;
}

