.Moveable {
    .multiStep {
        position: relative;
        overflow: hidden;
        margin-bottom: 35px;
        margin-top: 25px;
        border: 0;
        width: 100%;
        display: inline-block;
        line-height: 20px;
        @media (max-width: 1200px) {
            border-bottom: 1px solid #eee;
            border-right: 1px solid #eee;
        }
        &:after {
            position: absolute;
            bottom: 0;
            width: 100%;
            z-index: 0;
            height: 1px;
            left: 0;
            content: '';
            background: #eee;
            @media (max-width: 1200px) {
                position: static;
            }
        }
        & li {
            position: relative;
            z-index: 1;
            display: table;
            float: left;
            line-height: 43px;
            margin-left: -1px;
            margin-right: 0;
            border: 1px solid #eee;
            @media (max-width: 1200px) {
                width: 100%;
                border-right: 1px solid #eee;
                margin-bottom: -1px;
                margin-left: 0;
                box-sizing: border-box;
            }
            &:first-child {
                margin-left: 0;
            }
            & h4 {
                font-family: arial;
                font-size: 13px;
                letter-spacing: 0.08em;
                font-weight: 700;
                display: table-cell;
                padding: 0 30px;
                line-height: 41px;
                -webkit-transition: background .3s ease 0s;
                -moz-transition: background .3s ease 0s;
                -ms-transition: background .3s ease 0s;
                -o-transition: background .3s ease 0s;
                transition: background .3s ease 0s;
                vertical-align: middle;
                text-transform: uppercase;
                color: #1e1e1e;
                background: #f7f8f9;
                font-family: 'Poppins', Arial, Helvetica, sans-serif;
                text-decoration: none !important;
                @media (max-width: 480px) {
                    font-size: 12px;
                    padding: 0 15px;
                    letter-spacing: 0;
                }
                &:focus {
                    outline: none;
                }
            }
            &.ActivePage {
                border-bottom: 1px solid #fff;
                background: #fff;
                position: relative;
                z-index: 2;
                @media (max-width: 1200px) {
                    border: 1px solid #eee;
                }
            }
            &.ActivePage h4 {
                border-top: 0;
                border-bottom: 0;
                background: #fff;
                color: #1e1e1e;
                position: relative;
                &:before {
                    position: absolute;
                    top: 0;
                    width: 100%;
                    z-index: 999;
                    height: 3px;
                    left: 0;
                    content: '';
                    background: #000000;
                }
                @media (max-width: 1200px) {
                    background: #fff;
                    border-top: 0;
                }
            }
            &:hover {
                cursor: pointer;
            }
        }
    }
    .ExpressCheckoutBlock {
        border: 0;
        p {
            margin-top: 10px;
        }
    }
    .BillingDetails,
    .ShippingDetails,
    .OrderContents {
        h4 {
            letter-spacing: .05em;
            font-weight: 600;
            text-transform: uppercase;
            font-family: 'Poppins', Arial, Helvetica, sans-serif;
            font-size: 13px;
            color: #2d2d2d;
            background: #fff;
            line-height: 20px;
            padding: 0 0 10px 0;
        }
    }
}


