//
// Exact selectors below required to override `.table-striped` and prevent
// inheritance to nested tables.

// Generate the contextual variants
@include table-row-variant('active', $table-bg-active);
@include table-row-variant('success', $state-success-bg);
@include table-row-variant('info', $state-info-bg);
@include table-row-variant('warning', $state-warning-bg);
@include table-row-variant('danger', $state-danger-bg);


// Responsive tables
//
// Wrap your tables in `.table-responsive` and we'll make them mobile friendly
// by enabling horizontal scrolling. Only applies <768px. Everything above that
// will display normally.

.table-responsive {
  overflow-x: auto;
  min-height: 0.01%; // Workaround for IE9 bug (see https://github.com/twbs/bootstrap/issues/14837)

  @media screen and (max-width: $screen-xs-max) {
    width: 100%;
    margin-bottom: ($line-height-computed * 0.75);
    overflow-y: hidden;
    -ms-overflow-style: -ms-autohiding-scrollbar;
    border: 1px solid $table-border-color;

    // Tighten up spacing
    > .table {
      margin-bottom: 0;

      // Ensure the content doesn't wrap
      > thead,
      > tbody,
      > tfoot {
        > tr {
          > th,
          > td {
            white-space: nowrap;
          }
        }
      }
    }

    // Special overrides for the bordered tables
    > .table-bordered {
      border: 0;

      // Nuke the appropriate borders so that the parent can handle them
      > thead,
      > tbody,
      > tfoot {
        > tr {
          > th:first-child,
          > td:first-child {
            border-left: 0;
          }
          > th:last-child,
          > td:last-child {
            border-right: 0;
          }
        }
      }

      // Only nuke the last row's bottom-border in `tbody` and `tfoot` since
      // chances are there will be only one `tr` in a `thead` and that would
      // remove the border altogether.
      > tbody,
      > tfoot {
        > tr:last-child {
          > th,
          > td {
            border-bottom: 0;
          }
        }
      }

    }
  }
}

custom.scss:
/* Custom Styles */
// min-width: 990px
body.is-sticky {
    header.header {
        .navPages .navPages-action {
            .menu-label {
                display: none;
            }
        }
    }
    
    @media (min-width: 1200px) {
        .search-toggle, .header-right > .right-bottom #top-cart {
            margin-top: -1px;
        }
    }
    @media (min-width: 1025px) and (max-width: 1199px){
        .search-toggle, .header-right > .right-bottom #top-cart {
            margin-top: -3px;
        }
    }
    @media (max-width: 1449px ) and (min-width: 1025px){
        .search-toggle,.header-right > .right-bottom #top-cart {
            height: 51px;
        }
        header.header {
            position: fixed;
            top: 0;
            left: 0;
            right: 0;
            z-index: 1000;
            -webkit-animation-name: fadeInDown;
            animation-name: fadeInDown;
            -webkit-animation-duration: 0.4s;
            animation-duration: 0.4s;
            -webkit-animation-fill-mode: both;
            animation-fill-mode: both;
            padding: 0;

            @media (max-width: 1449px) and (min-width: 1025px) {
                & .header-left {
                    display: none;
                }
                & .header-center,
                & .header-bottom {
                    display: none;
                }

                & .header-right {
                    padding: 0;
                    width: auto;
                    right: 0;
                    position: absolute;
                    bottom: 0;
                    top: 0;
                    max-height: 54px;
                    & .right-bottom {
                        padding: 0;
                        height: 100%;
                        @media (max-width: 1449px) and (min-width: 1025px) {
                            .search-toggle.is-open {
                                top: 15px;
                            }
                            &>div:not(.top-cart) {
                                display: none;
                            }
                        }
                    }
                }
            }
        }
        & #menu.navPages-container {
            background: stencilColor('sticky-nav-bgColor');
            box-shadow: 0 4px 35px rgba(0, 0, 0, 0.1);
            -moz-box-shadow: 0 4px 35px rgba(0, 0, 0, 0.1);
            -webkit-box-shadow: 0 4px 35px rgba(0, 0, 0, 0.1);
            & nav ul.navPages-list>li>a {
                margin: 18px 9px 11px;
                color: stencilColor('sticky-nav-cart-color');
                &:hover{
                    color: stencilColor('sticky-nav-cart-color-hover');
                }
                &.has-subMenu::after {
                    color: stencilColor('sticky-nav-cart-color');
                }
                @media (max-width: 1449px) and (min-width: 1199px) {
                    font-size: 11px;
                    // padding-left: 10px;
                    // padding-right: 10px;
                    &.has-subMenu::after {
                        right: -1px;
                    }
                }
            }
            @media (max-width: 1449px) and (min-width: 1025px) {
                &>.container {
                    max-width: calc(100% - 200px);
                }
            }
        }

        & .sticky-logo {
            display: -webkit-box;
            display: -ms-flexbox;
            display: flex;
            position: absolute;
            margin: 0 20px;
            height: 52px;
            display: -ms-flexbox;
            display: -webkit-box;
            display: flex;
            -ms-flex-align: center;
            -webkit-box-align: center;
            align-items: center;
            img {
                height: 20px;
                padding: 3px;
                width: auto;
            }
        }
        @media (max-width: 1449px) and (min-width: 1025px) {
            & .top-cart {
                padding: 0 10px !important;
                height: 100%;
                &>a {
                    color: stencilColor('sticky-nav-cart-color');
                    height: 100%;
                    padding-right: 15px;
                    justify-content: center;
                    span.cart-quantity {
                        top: 10px !important;
                        right: 0 !important;
                        margin-top: 10px !important;
                        margin-left: 13px !important;
                    }
                    & i {
                        display: inline-block !important;
                        font-size: 16px;
                    }
                    & span:not(.cart-quantity) {
                        display: none;
                    }
                }
                & #cart-preview-dropdown {
                    display: none;
                    right: 0;
                    margin-top: 0;
                    & .triangle-with-shadow {
                        margin-left: 28px;
                    }
                }
                &:hover>a {
                    color: stencilColor('sticky-nav-cart-color-hover');
                }
            }
        }
    }

    @media (max-width: 1024px) {
        & .body {
            margin-top: 0 !important;
        }
    }
    @media (min-width: 1025px) and (max-width: 1199px){ 
        .navPages-container nav ul.navPages-list > li > a{
            font-size: 10px;   
            margin: 17px 5px 11px !important;
        }
    } 
}

