  &:before {
    color: $crumb-slash-color;
    content: "#{$crumb-slash}";
    margin: 0 $crumb-side-padding;
    position: relative;
    top: $crumb-slash-position;
  }

  &:first-child:before {
    content: " ";
    margin: 0;
  }
