// =============================================================================
// BRAND GRID (CSS)
// =============================================================================


// Brand grid view
// -----------------------------------------------------------------------------
//
// 1. Re-set the font-size, as we're reducing it to 0 in the .brandGrid class.
//
// -----------------------------------------------------------------------------
img.brand-logo-heading{
    border: 1px solid #d0d0d0;
    border-radius: 3px;
    margin-bottom: 30px;
    padding: 10px;
}
.brandGrid {

    @include u-listBullets("none");
    @include grid-row($behavior: "nest");
    font-size: 0; // 1
    margin-bottom: spacing("single");
    display: flex;
    flex-wrap: wrap;

    .brand {
        @include grid-column(6, $float: none);
        display: inline-block;
        font-size: fontSize("base"); // 1
        vertical-align: top;
        @include breakpoint("small") {
            width: grid-calc(4, $total-columns);
        }

        @include breakpoint("medium") {
            width: grid-calc(3, $total-columns);
        }
        figure {
            border: 1px solid #d0d0d0;
            border-radius: 3px;
            padding: 10px;
            min-height: 100px;
            display: flex;
            align-items: center;
            justify-content: center;
            width: 100%;
        }
        // scss-lint:disable SelectorDepth, NestingDepth
        .card-title {
            text-align: center;
            font-size: 14px;;
            letter-spacing: 0;
            font-weight: 700;
            font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
        }
    }
}

/*---------- Brand List A to Z view --------*/

#azBrandsTable {
    font-size: 0;
    display: flex;
    flex-flow: row nowrap;
    margin: 0 0 30px 0;
    overflow-x: auto;
    padding-bottom: 5px;
    li {
        display: inline-block;
        vertical-align: top;
        border: 1px solid #e2e4e8;
        border-left: none;
        a {
            color: #323232;
            display: block;
            height: 40px;
            font-size: 12px;
            min-width: 40px;
            font-weight: 600;
            line-height: 40px; 
            text-align: center;
            text-decoration: none;
            text-transform: uppercase;
        }
        &.disable a {
            color: #B3B3B3;
            pointer-events: none;
        }
        &:first-child {
            min-width: 70px;
            border-left: 1px solid #e2e4e8;
            /*border-radius: 3px 0 0 3px;*/
        }
        &:last-child {
            /*border-radius: 0 3px 3px 0;*/
        }
        &.is-active {
            color: stencilColor('color-textLink--hover');
            background-color: #f7f7f7;
            a {
                color: inherit;
            }
        }
    }
    @media (min-width: 1025px) {
        margin: 0 0 40px 0;
        overflow: visible;
        li {
            width: 10%;
        }
    }
}

#azBrands{
    &.active-all {
        column-count: 2;
        -moz-column-count: 2;
        -webkit-column-count: 2;
        .azBrands-group {
            display: block;
            -moz-page-break-inside: avoid;
            -webkit-column-break-inside: avoid;
            break-inside: avoid;
            -o-column-break-inside: avoid;
            -ms-column-break-inside: avoid;
            column-break-inside: avoid;
            page-break-inside: avoid;
        }
        @include breakpoint('small') {
            column-count: 3;
            -moz-column-count: 3;
            -webkit-column-count: 3;
        }
        @include breakpoint('medium') {
            column-count: 4;
            -moz-column-count: 4;
            -webkit-column-count: 4;
        }
    }
    &:not(.active-all) {
        .azBrands-group {
            column-count: 2;
            -moz-column-count: 2;
            -webkit-column-count: 2;
            @include breakpoint('small') {
                column-count: 3;
                -moz-column-count: 3;
                -webkit-column-count: 3;
            }
            @include breakpoint('medium') {
                column-count: 4;
                -moz-column-count: 4;
                -webkit-column-count: 4;
            }
        }
    }
    .brand-name .title {
        color: #646464;
    }
    + .brandList.hide {
        opacity: 0;
        visibility: hidden;
    }
}

.azBrands-group-title {
    font-size: 20px;
    margin: 0 0 10px 0;
    text-transform: uppercase;
    font-family: $fontFamily-headings;
    color: stencilColor('color-textHeading');
    font-weight: 700;
}

.brandList {
    margin: 0;
    list-style: none;
    .brand {
        margin-bottom: 5px;
    }
}

.brand-name {
    .title {
        margin: 0;
        font-size: 12px;
        line-height: 20px;
        font-weight: 400;
        color: #323232;
        font-family: $fontFamily-sans;
        text-transform: capitalize;
        a {
            color: inherit;
        }
    }
}

.azBrands-group {
    margin-bottom: 30px;
    display: none;
    &.is-active {
        display: block;
    }
}

