}

layouts/footer/footer.scss:
// =============================================================================
// FOOTER (CSS)
// =============================================================================
#footer-wrapper {
    padding: 77px 0px 135px;
    background: stencilColor('footer-backgroundColor');
    @media (max-width: 767px) {
        padding-top: 55px;
    }
}

/*Main Footer*/

#main-footer .container {
    @media (max-width: 991px) {
        padding-bottom: 0;
    }
}

#main-footer .col-1,
#main-footer .col-2,
#main-footer .col-3 {
    float: left;
    width: 20%;
    padding: 0 15px;
    @media (max-width: 991px) {
        width: 33.33%;
        margin-bottom: 20px;
    }
    @media (max-width: 480px) {
        width: 50%;
    }
    @media (max-width: 320px) {
        width: 100%;
    }
}

@media (max-width: 480px) {
    #main-footer .col-3 {
        width: 100%;
    }
}

#main-footer .col-4 {
    float: left;
    width: 40%;
    padding: 0 15px;
    @media (max-width: 991px) {
        width: 100%;
    }
}

#main-footer h3 {
    font-family: $fontFamily-headings;
    font-size: 13px;
    font-weight: stencilFontWeight('headings-font');
    margin: 0;
    margin-bottom: 16px;
    text-transform: uppercase;
    color: stencilColor('footer-column-heading-color');
    font-weight: 700;
    letter-spacing: 0.13em;
    &.social-heading {
        font-family: $fontFamily-sans;
        font-size: fontSize("base");
        letter-spacing: .02em;
        text-transform: none;
    }
}

.footer-columns {
    margin: 0;
    padding: 0;
    display: flex;
    flex-wrap: wrap;
    & .column-item {
        width: 20%;
        padding-right: 15px;
        padding-bottom: 35px;
        padding-left: 15px;
        @media (max-width: 767px) {
            padding-bottom: 10px;
        }
        &.last {
            width: 33.3333%;
            @media (max-width: 1199px) {
                width: 100%;
                max-width: 480px;
            }
            @media (max-width: 767px) {
                margin-top: 5px;
                h3 {
                    margin-bottom: 5px;
                }
            }
        }
        @media (max-width: 991px) {
            width: 33.33%;
        }
        @media (max-width: 767px) {
            width: 100%;
        }
        .footer-info-heading {
            padding-bottom: 20px;
            border-bottom: 1px solid #fff;
            @media (min-width: 768px) {
                pointer-events: none;
            }
        }
        @media (max-width: 767px) {
            .footer-info-heading {
                margin-bottom: 0;
                padding: 8px 0;
                border-bottom: 1px solid rgba(255,255,255,0.3);
                position: relative;
                cursor: pointer;
                &:before {
                    right: 0;
                    top: 6px;
                    z-index: 2;
                    content: "\f107";
                    font-family: FontAwesome;
                    color: #fff;
                    font-size: 20px;
                    line-height: 1;
                    font-weight: 500;
                    position: absolute;
                }
            }
            &.open-dropdown {
                .footer-info-heading {
                    &:before {
                        content: "\f106";
                    }
                }
            }
        }
        & ul {
            margin-left: 0;
            list-style: none;
            &.footer-info-list {
                @media (min-width: 768px) {
                    display: block !important;
                }
            }
            & li {
                padding: 10px 0px 0px 0px;
                & a {
                    color: stencilColor('footer-link-color');
                    &:hover {
                        border-bottom: 1px dotted;
                        line-height: 1.3;
                    }
                }
                &.all-brands-link {
                    padding-top: 8px;
                    & a {
                        font-family: $fontFamily-headings;
                        font-size: 11px;
                        font-weight: stencilFontWeight('headings-font');
                        letter-spacing: $headings-letter-spacing;
                        text-transform: uppercase;
                        color: stencilColor('footer-link-color');
                        & i.fa {
                            font-size: 13px;
                            margin-right: 5px;
                            vertical-align: -1px;
                        }
                        &:hover {
                            color: $anchor-font-color-hover;
                        }
                    }
                }
            }
        }
        @media (max-width: 767px) {
            & .footer-info-list {
                display: none;
            }
        }
        & .social-icons {
            margin: 0 0 5px;
            padding: 0;
            li {
                margin-right: 15px;
                &:first-child .icon--facebook {
                    margin-left: -6px;
                }
            }
            & a {
                width: 25px;
                height: 38px;
                &:not(:hover) {
                    background: transparent;
                }
                & svg {
                    width: 23px;
                    fill: #fff;
                }
            }
        }
    }
}

.bottom-columns {
    padding-top: 50px;
    display: flex;
    flex-wrap: wrap;
    & .column-item {
        width: 33.33%;
        margin-bottom: 25px;
        padding-bottom: 25px;
        text-align: center;
        @media (max-width: 767px) {
            width: 100%;
            padding-bottom: 15px;
            &:last-child {
                border: 0;
            }
        }
        & ul {
            margin-left: 0;
            list-style: none;
            display: flex;
            justify-content: center;
            align-items: center;
            flex-wrap: wrap;
            & li {
                display: inline-block;
            }
        }
        & .social-icons {
            margin: 0;
            padding: 0;
            & a {
                width: 38px;
                height: 38px;
                &:not(:hover) {
                    background: #2a2a2e;
                }
                & svg {
                    width: 16px;
                    fill: #fff;
                }
            }
        }
        &.second {
            & ul {
                & li {
                    &+li {
                        margin-left: 10px;
                    }
                }
            }
        }
    }
    & p {
        color: stencilColor('footer-text-color');
    }
}


/*Mailist Box - Newsletter Sign Up*/

.footer-newsletter {
    text-align: center;
    padding-bottom: 91px;
    @media (max-width: 767px) {
        padding-bottom: 45px;
    }
}

#footerSubcribeForm {
    margin-bottom: 18px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: center;
    .btn-subcribe {
        height: 50px;
        padding-top: 5px;
        padding: 4px 15px 5px 15px;
        svg {
            width: 45px;
            height: 20px;
            fill: #fff;
        }
    }
    h3 {
        margin-bottom: 20px;
        font-size: 22px;
        text-transform: uppercase;
        font-weight: 600;
        letter-spacing: 0.14em;
        color: stencilColor('footer-text-color');
        width: 100%;
        @media (max-width: 767px) {
            line-height: 26px;
        }
    }
    .desNewsletter {
        text-align: center;
        width: 100%;
        p {
            margin-bottom: 28px;
            letter-spacing: 0.05em;
            color: stencilColor('footer-text-color');
        }
    }
    &>form {
        width: 100%;
        margin-top: 5px;
        margin-bottom: 5px;
        max-width: 570px;
        margin: auto;
        height: 54px;
        line-height: 54px;
        border: 2px solid #fff;
    }
    &>form {
        & .form-field {
            margin-bottom: 0;
            & .form-wrap {
                display: flex;
                flex-wrap: no-wrap;
                @media (max-width: 480px) {
                    flex-wrap: wrap;
                }
            }
        }
    }
    & input[type='text'],
    & input[type='email'] {
        display: inline-block;
        width: 100%;
        margin-right: 10px;
        height: 50px;
        padding: 0 20px;
        vertical-align: middle;
        border: 0;
        background-color: transparent;
        letter-spacing: 0.05em;
        font-weight: 500;
        font-size: fontSize("base");
        color: #fff;
       &::-webkit-input-placeholder {
            /* Chrome/Opera/Safari */
            color: #ffffff;
        }
        &::-moz-placeholder {
            /* Firefox 19+ */
            color: #ffffff;
        }
        &:-ms-input-placeholder {
            /* IE 10+ */
            color: #ffffff;
        }
        &:-moz-placeholder {
            /* Firefox 18- */
            color: #ffffff;
        }
        @media (max-width: 480px) {
                width: calc(100% - 75px);
            margin-right: 0;
        }
    }

    & input[type='submit'] {
        width: auto;
        margin-top: 7px;
        margin-bottom: 7px;
        padding-right: 22px;
        padding-left: 22px;
        vertical-align: middle;
        color: stencilColor('footer-subscribe-button-color');
        @media (min-width: 992px) and (max-width: 1199px) {
            padding-right: 20px;
            padding-left: 20px;
        }
        @media (max-width: 480px) {
            width: 100%;
        }
    }

    & input[type='submit']:hover {
        color: stencilColor('footer-subscribe-button-colorHover');
        background: stencilColor('footer-subscribe-button-bgColorHover');
    }
}




/*Bottom Footer*/

#bottom-footer {
    padding: 35px 0 25px;
    color: stencilColor('footer-text-color');
    & .container>div {
        display: flex;
        justify-content: center;
        &>div {
            padding: 5px 0;
            text-align: center;
                font-weight: 400;
                    line-height: 24px;
                        width: 100%;
        }
        @media (max-width: 991px) {
            justify-content: flex-start;
            flex-wrap: wrap;
            & .col-left {
                width: 100%;
            }
            & .col-right {
                width: 100%;
            }
            &>ul {
                padding: 5px 0;
            }
        }
    }
}

#bottom-footer .footer-copyright,
#bottom-footer .powered-by {
    display: inline-block;
}

#bottom-footer .footer-copyright p,
#bottom-footer .powered-by p,
#bottom-footer .template-by p {
    margin-bottom: 0;
}



#bottom-footer ul.custom-links {
    margin: 0;
    list-style: none;
    & li {
        display: inline-block;
        &+li::before {
            display: inline-block;
            position: relative;
            top: -1px;
            width: 1px;
            height: 16px;
            margin-right: 10px;
            margin-left: 8px;
            content: '';
            vertical-align: middle;
            background: #d0d0d0;
        }
    }
}

#bottom-footer a {
    color: stencilColor('footer-link-color');
}

#bottom-footer a:hover {
    color: $anchor-font-color-hover;
}

.footer-payment-icons {
    padding: 20px 0;
    margin-top: 35px;
    display: -webkit-box;
    /* OLD - iOS 6-, Safari 3.1-6 */
    display: -moz-box;
    /* OLD - Firefox 19- (buggy but mostly works) */
    display: -ms-flexbox;
    /* TWEENER - IE 10 */
    display: -webkit-flex;
    /* NEW - Chrome */
    display: flex;
    /* NEW, Spec - Opera 12.1, Firefox 20+ */
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    .ips {
        font-size: 0;
        padding: 0;
        &:hover {
            opacity: .5;
        }
    }
    .ips:before {
        display: inline-block;
        width: 120px;
        height: 40px;
        content: ' ';
        vertical-align: middle;
        background-image: url('../icons/payment-grey-sprite.svg');
        background-repeat: no-repeat;
        -webkit-background-size: 120px;
      -moz-background-size: 120px;
      -o-background-size: 120px;
      background-size: 120px;
    }
    .ips.icon-card-paypal:before {
        width: 67px;
        background-position: center -4px;
    }
    .ips.icon-card-visa:before {
        width: 80px;
        background-position: center -45px;
    }
    .ips.icon-card-mastercard:before {
        width: 60px;
        background-position: center -83px;
    }
    .ips.icon-card-amex-square:before {
        width: 80px;
        background-position: center -123px;
    }
    .ips.icon-card-discover:before {
        width: 70px;
        background-position: center -162px;
    }
    .ips.icon-card-maestro:before {
        width: 90px;
        background-position: center -202px;
    }
    .ips.icon-card-google-wallet:before {
        background-position: center -240px;
    }
}

/*Accept Cookies*/

#accept-cookies {
    position: fixed;
    z-index: 9999;
    right: 10px;
    bottom: 10px;
    width: 100%;
    min-width: 300px;
    max-width: 390px;
    min-height: 200px;
    padding: 30px 30px 20px 16px;
    border: 1px solid #000;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    background: #141414;
    @media (max-width: 400px) {
        max-width: 354px;
    }
}

#accept-cookies .cookies-inner {
    padding-left: 54px;
    background: url('../img/cookie-info.png') top left no-repeat;
}

#accept-cookies p {
    font-style: italic;
    position: relative;
    top: -5px;
    letter-spacing: .02em;
    color: #fff;
}

#accept-cookies button {
    font-size: 11px;
    padding: 0 16px;
    letter-spacing: .05em;
}



/*Scroll To Top*/

#back-to-top {
    font-size: 36px;
    position: fixed;
    z-index: 10;
    bottom: 30%;
    right: 20px;
    visibility: hidden;
    /* image replacement properties */
    overflow: hidden;
    width: 36px;
    height: 36px;
    /* Opacity for IE8 and lower */
    -webkit-transition: opacity .3s 0s, visibility 0s .3s;
    -moz-transition: opacity .3s 0s, visibility 0s .3s;
    transition: opacity .3s 0s, visibility 0s .3s;
    white-space: nowrap;
    opacity: 0;
    color: #000;
    border: 2px solid #000;
    display: flex;
    justify-content: center;
    /* Opacity for Modern Browsers */
    filter: alpha(opacity=0);
    & i.fa {
        position: relative;
        top: -4px;
    }
    @media (max-width: 991px) {
        background: #000;
        color: #fff;
        bottom: 150px;
        right: 20px;
    }
}

#back-to-top.is-visible,
#back-to-top.fade-out,
.no-touch #back-to-top:hover {
    -webkit-transition: opacity .3s 0s, visibility 0s 0s;
    -moz-transition: opacity .3s 0s, visibility 0s 0s;
    transition: opacity .3s 0s, visibility 0s 0s;
}

#back-to-top.is-visible {
    /* the button becomes visible */
    visibility: visible;
    opacity: 1;
    /* Opacity for Modern Browsers */
    filter: alpha(opacity=100);
    /* Opacity for IE8 and lower */
}

#back-to-top.fade-out {
    /* if the user keeps scrolling down, the button is out of focus and becomes less visible */
    opacity: .5;
    /* Opacity for Modern Browsers */
    filter: alpha(opacity=50);
    /* Opacity for IE8 and lower */
    &:hover {
        opacity: 1;
        filter: alpha(opacity=100);
    }
}

layouts/sidebar/block.scss:
.page-type-category .page,
.page-type-brand .page,
.page-type-search .page {
    display: -webkit-box; 
    display: -moz-box;
    display: -ms-flexbox;
    display: -webkit-flex; 
    display: flex;
    flex-wrap: nowrap;
    margin: 0;
    @media (max-width: 767px) {
        flex-wrap: wrap;
    }
}

// =============================================================================
// SIDEBAR BLOCKS (CSS)
// =============================================================================
aside.page-sidebar.col-sm-3 {
    width: 270px;
    padding: 0;
    @media (min-width: 992px) and (max-width: 1199px) {
        width: 25%;
    }
    @media (min-width: 768px) and (max-width: 991px) {
        width: 30%;
    }
    @media (max-width: 767px) {
        width: 100%;
    }
}

@media (min-width: 768px) {
    aside.page-sidebar {
        &>nav {
            display: block !important;
        }
    }
}

@media (max-width: 767px) {
    aside.page-sidebar {
        &>nav {
            display: none;
        }
    }
}

@media (max-width: 767px) {
    #sidebar-toggle {
        display: block !important;
        margin-bottom: 25px;
        & a {
            font-weight: 700;
            display: flex;
            flex-wrap: nowrap;
            text-transform: uppercase;
            justify-content: center;
            & i {
                font-size: 14px;
                margin-left: 10px;
            }
        }
    }
}

aside.page-sidebar.col-sm-3~main.page-content.col-sm-9 {
    width: calc(100% - 270px);
    padding-left: 30px;
    padding-right: 0;
    @media (min-width: 992px) and (max-width: 1199px) {
        width: 75%;
    }
    @media (min-width: 768px) and (max-width: 991px) {
        width: 70%;
    }
    @media (max-width: 767px) {
        width: 100%;
        padding-left: 0;
    }
}

.sidebarBlock {
    margin-bottom: 17px;
    clear: both;
    & .side-module-heading {
        font-family: $fontFamily-headings;
        font-size: 15px;
        letter-spacing: 0.05em;
        font-weight: 700;
        margin-top: 0;
        margin-bottom: 30px;
        padding-top: 10px;
        padding-bottom: 22px;
        text-transform: capitalize;
        border-bottom: 1px solid #eeeeee;
    }
    ul.navList>li.navList-item {
        padding: 0 0 22px;
        &>a.navList-action {
            padding: 0;
            display: inline;
            font-weight: 600;
            &:hover {
                border-bottom: 1px dotted $anchor-font-color-hover;
            }
        }
    }
}

#sideBannerImage.sidebarBlock {
    padding-top: 0;
    margin-bottom: 30px;
    text-align: center;
}

h5.shop-by-heading {
    font-size: 16px;
    text-transform: none;
    margin: 0;
    padding-top: 11px;
    padding-bottom: 15px;
}

#sideAllCategories {
    padding-top: 3px;
    margin-bottom: 25px;
    & .side-module-heading {
        margin-bottom: 11px;
    }
}

ul.all-categories-list {
    margin: 0;
    list-style: none;
    & li {
        position: relative;
        padding: 10px 0;
        -moz-transition: all 0.3s ease 0s;
        -webkit-transition: all 0.3s ease 0s;
        transition: all 0.3s ease 0s;
        &:last-child {
            border-bottom: 0;
        }
        & a {
            display: inline;
            position: relative;
            color: #464646;
            &:hover {
                text-decoration: none;
                color: #000;
                border-bottom: 1px dotted;
            }
        }
    }
    & i.fa.fa-angle-down {
        position: absolute;
        right: 0;
        padding: 11px 0;
        width: 100%;
        text-align: right;
        top: -2px;
        cursor: pointer;
        font-size: 18px;
        color: #cfcfcf;
        &.is-clicked::before {
            content: "\f106";
            position: relative;
            top: -1px;
            color: #000000;
        }
    }
    & ul.dropdown-category-list {
        opacity: 0;
        visibility: hidden;
        height: 0;
        overflow: hidden;
        margin: 0;
        -moz-transition: all 0.3s ease 0s;
        -webkit-transition: all 0.3s ease 0s;
        transition: all 0.3s ease 0s;
        list-style: none;
        &>li:hover {
            padding-left: 33px !important;
            -moz-transition: all 0.3s ease 0s;
            -webkit-transition: all 0.3s ease 0s;
            transition: all 0.3s ease 0s;
        }
        &>li {
            &>ul {
                // margin-left: -33px;
            }
        }
        &.cat-expanded {
            opacity: 1;
            visibility: visible;
            height: auto;
            margin-top: 8px;
            -moz-transition: all 0.3s ease 0s;
            -webkit-transition: all 0.3s ease 0s;
            transition: all 0.3s ease 0s;
        }
    }
    &>li {
        &>a {
            font-family: $fontFamily-headings;
            font-size: 13px;
            letter-spacing: 0.02em;
            text-transform: capitalize;
            font-weight: 600;
            color: #000;
            &::before {
                display: none;
            }
        }
        &>i.fa.fa-angle-down {
            top: 0;
            color: #000;
        }
        &>ul.dropdown-category-list.cat-expanded {
            padding-top: 1px;
        }
    }
    & ul#navPages-level-2>li {
        &>a {
            font-size: 13px;
            font-weight: 600;
            color: #000;
            letter-spacing: 0.02em;
        }
        &.current-cat {
            padding-left: 33px !important;
            &>a::before {
                content: "";
                display: inline-block;
                width: 20px;
                height: 2px;
                background-color: #ff7677;
                position: absolute;
                left: -33px;
                top: 9px;
            }
        }
        &:hover {
            &>a::before {
                content: "";
                display: inline-block;
                width: 20px;
                height: 2px;
                background-color: #ff7677;
                position: absolute;
                left: -33px;
                top: 9px;
            }
        }
    }
    li.current-cat>a {
        color: $anchor-font-color-hover !important;
        border-bottom: 1px dotted #8d8d8d;
