                    }
                    &>a img {
                        margin-right: 10px;

                        vertical-align: -1px;
                    }
                    &>a .fa {
                        margin-left: 10px;
                    }
                    &>a::before {
                        @media (max-width: 1024px) {
                            display: none;
                        }
                    }
                }

                &#accountDropdown {
                    position: relative;
                    &>a {
                        & .fa {
                            margin-left: 4px;
                        }
                    }
                    &>ul {
                        top: auto !important;
                        left: 50% !important;

                        min-width: 200px;
                        margin-top: 9px;
                        padding-top: 6px;
                        padding-bottom: 6px;

                        -webkit-transform: translateX(-50%);
                        -moz-transform: translateX(-50%);
                        -ms-transform: translateX(-50%);
                        -o-transform: translateX(-50%);
                        transform: translateX(-50%);
                        text-align: left;

                        border-radius: 3px;
                        &>li {
                            display: block;

                            padding: 0;
                            &>a {
                                display: block;

                                padding: 7px 15px;
                                &:hover {
                                    text-decoration: none;
                                    background-color: #f5f5f5;
                                }
                                & img {
                                    margin-right: 5px;

                                    vertical-align: -1px;
                                }
                            }
                        }
                        &>li+li {
                            border-top: 1px solid #f3f3f3;
