            }
        }
    }
}


.header-right {
    width: 450px;
    float: right;
    @media(min-width: 1025px) {
        display: -ms-flexbox;
        display: -webkit-flex;
        display: flex;
        height: 55px;
        justify-content: flex-end;
        flex-direction: column;
        justify-content: flex-end;
        align-content: flex-end;
    }
    @media (max-width: 1024px) {
        width: 62px;
        height: 55px;
        padding-top: 0;
        padding-bottom: 0;
    }
    @media (max-width: 380px) {
        width: 45px;
    }
    &>.right-bottom {
        display: flex;
        justify-content: flex-end;
        align-items: center;
        @media (min-width: 1025px) {
            .account-link,
            .gift_certificates-link,
            .wishlist-link,
            #top-cart {
                height: 55px;
                border-left: 1px solid stencilColor('headerTop-borderColor');
                 display: -webkit-box;      /* OLD - iOS 6-, Safari 3.1-6 */
                  display: -moz-box;         /* OLD - Firefox 19- (buggy but mostly works) */
                  display: -ms-flexbox;      /* TWEENER - IE 10 */
                  display: -webkit-flex;     /* NEW - Chrome */
                  display: flex;             /* NEW, Spec - Opera 12.1, Firefox 20+ */
                -ms-flex-align: center;
                -webkit-align-items: center;
                align-items: center;
                padding: 0px 21px;
            }
        }

        &>div>a {
            font-family: $fontFamily-headings;
            font-size: 11px;
            font-weight: 600;
            white-space: nowrap;
            letter-spacing: 0.1em;
            text-transform: uppercase;
            transition: none;
            display: flex;
            align-items: center;
            color: stencilColor('headerTop-textColor');
            span {
                line-height: 1.3;
                border-bottom: 1px dotted transparent;
                padding-top: 2px;
            }
            &:hover {
                span {
                    border-bottom: 1px dotted;
                }
            }
        }
        & #top-cart {
            @media (max-width: 1024px) {
                display: -webkit-box;
                display: -ms-flexbox;
                display: inline-flex;
                -webkit-box-align: center;
                -ms-flex-align: center;
                align-items: center;
                text-align: center;
                width: 100%;
                height: 55px;
                
            }
            &>a {
                position: relative;
                @media (max-width: 1024px) {
                    width: 20px;
                    display: inline-block;
                    margin: auto;
                    height: 24px;
                }
                & i.icon-cart {
                    height: 24px;
                    @media (min-width: 1025px){
                        margin-left: 15px;
                    }
                    svg {
                        fill: stencilColor('headerTop-textColor');
                        width: 20px;
                        height: 24px;
                        @media (max-width: 380px) {
                            width: 18px;
                            height: 20px;
                        }
                    }
                }
                &:hover{
                    & span.cart-quantity {
                        border-bottom: 0;
                    }
                }
                & span.cart-quantity {
                    font-family: $fontFamily-sans;
                    font-weight: 400;
                    background-color: stencilColor('item-count-circle-backgroundColor');
                    color: stencilColor('item-count-circle-color');                    
                   display: -ms-flexbox;
                    display: -webkit-flex;
                    display: flex;
                    -ms-flex-align: center;
                    -webkit-align-items: center;
                    align-items: center;
                    -ms-flex-pack: center;
                    -webkit-justify-content: center;
                    justify-content: center;
                    position: absolute;
                    top: -2px;
                    right: -13px;
                    position: absolute;
                    right: auto;
                    left: 50%;
                    top: 50%;
                    -webkit-transform: translate(-50%,-50%);
                    -ms-transform: translate(-50%,-50%);
                    transform: translate(-50%,-50%);
                    margin-top: -6px;
                    margin-left: 35px;
                    line-height: 22px;
                    display: inline-block;
                    min-width: 22px;
                    padding: 0 3px 0 4px;
                    text-align: center;
                    border-radius: 50%;
                    font-size: 11px;
                    border: 0;

                    @media(max-width: 1024px){
                        margin-left: 14px;
                    }
                    @media(max-width: 767px){
                        margin-left: 10px;
                        margin-top: -7px;
                        padding: 0 2px 0 4px;
                    }
                    @media(max-width: 380px){
                        min-width: 20px;
                        line-height: 20px;
                    }
                }
                @-moz-document url-prefix() {
                  span.cart-quantity {
                    padding: 0 3px 0 4px;
                  }
                }
            }
        }
    }
}

.header-center {
