layouts/body/body.scss:
// =============================================================================
// BODY (CSS)
//
// =============================================================================

body {
    background-color: stencilColor('body-backgroundColor');
    line-height: 20px;

    &::-webkit-scrollbar {
        width: 10px;
    }
    &::-webkit-scrollbar-track {
        background: #e5e5e5;
    }
    &::-webkit-scrollbar-thumb {
        background: #323232;
    }
}
a {
    &:hover,
    &:focus,
    &:active {
        outline: none;
        text-decoration: none;
    }
}
.hidden-mobile {
    display: none;
    @media (min-width: 1025px) {
        display: block;
    }
}
.hidden-desktop {
    display: block;
    @media (min-width: 1025px) {
        display: none;
    }
}
/*Off-canvas Navigation*/
@media (max-width: 1024px) {
    #st-trigger-effects {
        width: 62px;
        height: 55px;
        text-align: center;
        display: flex !important;
        align-content: stretch;
        align-items: stretch;
        float: left;
        & a {
            width: 100%;
            height: auto;
            display: flex;
            align-content: center;
            align-items: center;
            justify-content: center;
            font-size: 22px;
            position: relative;
            & svg {
                width: 21px;
                height: 21px;
                margin-left: -5px;
                fill: stencilColor('mobile_sidebar_trigger');               
            }
            &.close-canvas{
                display: none;
            }
        }
        &:hover a svg {
            fill: stencilColor('mobile_sidebar_triggerHover');
        }
    }
}

.hamburger-menu,
.hamburger-menu:after,
.hamburger-menu:before {
    width: 24px;
    height: 2px;
}

.hamburger-menu {
    background: #000000;
    transition: all 0ms 300ms;
    border: none;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
}

.hamburger-menu:before {
    content: "";
    position: absolute;
    left: 0;
    bottom: 8px;
    background: #000000;
    transition: bottom 300ms 300ms cubic-bezier(0.23, 1, 0.32, 1), transform 300ms cubic-bezier(0.23, 1, 0.32, 1);
}

.hamburger-menu:after {
    content: "";
    position: absolute;
    left: 0;
    top: 8px;
    background: #000000;
    transition: top 300ms 300ms cubic-bezier(0.23, 1, 0.32, 1), transform 300ms cubic-bezier(0.23, 1, 0.32, 1);
}


body.st-off-canvas {
    overflow: hidden;
    & #st-trigger-effects {
        a{  
            display: none;
            & svg {
                width: 25px;
                height: 25px;
            }
        }
        
        a.close-canvas{
            display: inline-block;
            position: relative;
            z-index: 9999;
            padding: 10px;
            top: 0;
            & svg {
                fill: stencilColor('mobile_sidebar_triggerHover');
            }
        }
    }
}

@media (max-width: 380px) {
    #st-trigger-effects {
        width: 50px;
    }
    .hamburger-menu, 
    .hamburger-menu::after, 
    .hamburger-menu::before {
        width: 20px;
    }
    .hamburger-menu::before {
        bottom: 6px;
    }
    .hamburger-menu::after {
        top: 6px;
    }
}

.st-container,
.st-pusher,
.st-content {
    height: 100%;
}

.st-content {
    overflow-y: scroll;
    background: #f3efe0;
}

.st-content,
.st-content-inner {
    position: relative;
}

.st-container {
    position: relative;
    overflow: hidden;
}

.st-pusher {
    position: relative;
    left: 0;
    height: 100%;
    -webkit-transition: -webkit-transform .3s;
    -moz-transition: -webkit-transform .3s;
    -ms-transition: -webkit-transform .3s;
    -o-transition: -webkit-transform .3s;
    transition: transform .3s;
    max-width: $boxed-width;
    margin: 0 auto;
    background: #fff;
}

.navPages-action-moreIcon {
    @media (min-width: 1025px) {
        display: none;
    }
    @media (max-width: 1024px) {
        top: 0;
        right: 0;
        bottom: 0;
        margin: 0;
        width: 100%;
        height: 42px;
        z-index: 10;
        position: absolute;
        display: -ms-flexbox;
        display: -webkit-box;
        display: flex;
        -ms-flex-align: center;
        -webkit-box-align: center;
        align-items: center;
        -ms-flex-pack: flex-end;
        -webkit-box-pack: end;
        justify-content: flex-end;
        -ms-flex-wrap: wrap;
        flex-wrap: wrap;
        svg {
            width: 12px;
            height: 12px;
            fill: #969696;
            stroke: #969696;
            transition: all .5s ease;
            transform: rotate(-90deg);
            -ms-transform: rotate(-90deg);
            -webkit-transform: rotate(-90deg);
            -moz-transform: rotate(-90deg);
            -o-transform: rotate(-90deg);
            margin-right: 15px;
        }
    }
}

.navPages-action {
    @media (max-width: 1024px) {
        position: relative;
    }
    > span {
        position: relative;
    }
    &.is-open {
       >  .navPages-action-moreIcon {
            svg {
                transition: all .5s ease;
                transform: rotate(-270deg);
                -ms-transform: rotate(-270deg);
                -webkit-transform: rotate(-270deg);
                -moz-transform: rotate(-270deg);
                -o-transform: rotate(-270deg);
            }
            &:after {
                content: "Back";
                position: absolute;
                top: 12px;
                left: 32px;
                color: #969696;
                font-style: normal;
                font-size: 12px;
                font-weight: 600;
                text-transform: uppercase;
            }
        }
        &.is-hidden {
            display: none;
        }
    }
}

.st-menu {
    position: fixed;
    z-index: 1001;
    top: 0;
    left: -100%;
    visibility: hidden;
    width: 380px;
    height: 100%;
    -webkit-transition: all .3s;
    transition: all .3s;
    box-shadow: inset -16px -1px 8px -18px #BFBFBF;
    -webkit-box-shadow: inset -16px -1px 8px -18px #BFBFBF;
    -moz-box-shadow: inset -16px -1px 8px -18px #BFBFBF;
    -o-box-shadow: inset -16px -1px 8px -18px #BFBFBF;
    border-right: 1px solid #e5e5e5;
    background: #ffffff;
    @media (max-width: 767px) {
        width: calc(100% - 41px);
        max-width: 380px;
    }
    .navPages-list {
        li {
            &.is-hidden {
                display: none;
            }
            &.is-open {
                .navPages-action {
                    &.is-open {
                        text-align: right;
                        background: #fafafa;
                        .navPages-action-moreIcon {
                            left: 0;
                            right: auto;
                            width: 100%;
                            -ms-flex-pack: flex-start;
                            -webkit-box-pack: start;
                            justify-content: flex-start;
                            left: 0px;
                            z-index: 2;
                            color: #969696;
                            padding-left: 10px;
                        }
                        + .navPage-subMenu {
                            display: block;
                        }
                    }
                }
            }
            .navPage-subMenu {
                .navPage-subMenu-list {
                    li {
                        a {
                            position: relative;
                        }
                        ul {
                            display: none;
                        }
                        &.is-open {
                            > a {
                                text-transform: uppercase;
                                font-size: 13px;
                            }
                            ul {
                                display: block;
                            }
                        }
                    }
                }
                
            }
        }
    }
    .subMegaMenu {
        .imageArea {
            &.is-hidden {
                display: none;
            }
            .col-right {
                a {
                    &:last-child {
                        border-bottom: none;
                    }
                }
            }
        }
    }
}

.bg-mobile {
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    opacity: 0;
    content: "";
    position: absolute;
    background-color: rgba(0,0,0,.3);
    z-index: -1;
}
.has-activeNavPages {
    .st-menu {
        &.is-open {
            visibility: visible;
            left: 0;
        }
    }
    .bg-mobile {
        z-index: 999;
        display: block;
        opacity: 1;
        visibility: visible;
    }
}
.themevale_open-Account {
    .st-menu {
        &.is-open {
            visibility: visible;
            left: 0;
        }
    }
    .bg-mobile {
        z-index: 999;
        display: block;
        opacity: 1;
        visibility: visible;
    }
}

.st-menu .themevale_close {
    top: 0;
    right: -42px;
    width: 41px;
    height: 41px;
    z-index: 10;
    line-height: 41px;
    position: absolute;
    text-align: center;
    background-color: #000;
}
.st-menu .themevale_close .close {
    opacity: 1;
    width: 100%;
    height: 100%;
    color: #fff;
    display: block;
    font-size: 34px;
    font-weight: 300;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
   -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    -webkit-text-shadow: none;
    -moz-text-shadow: none;
    -ms-text-shadow: none;
    text-shadow: none;
    transition: none;
    -ms-transition: none;
     -webkit-transition: none;
    -moz-transition: none;
    -o-transition: none;
}

/* content style */

