        background-repeat: no-repeat;
        background-size: 100%;
        display: block;
        height: remCalc(41px);
        width: remCalc(20px);
    }

    &:hover,
    &:focus {
        @include carouselOpaqueBackgrounds($slick-arrow-bgColor); // 1
