components/foundation/pagination/paginat:
// =============================================================================
// PAGINATION (CSS)
// =============================================================================
// Pagination list items
// -----------------------------------------------------------------------------
//
// 1. Centred list via inline-block. Font-size: 0; to remove white-space.
// 2. Specificity overrides for Foundation's inadequate selectors.
//
// -----------------------------------------------------------------------------
.product-pagination {
    margin-bottom: 25px;
    text-align: right;
    &.top {
        @media (max-width: 991px) {
            justify-content: space-between;
        }
    }
    &.top .pagination {
        width: calc(100% - 300px);
        text-align: center;
        @media (max-width: 991px) {
            order: 3;
            width: 100%;
            padding-top: 15px;
        }
    }
    &.bottom {
        padding-top: 30px;
        border-top: 1px solid #e5e5e5;
        position: relative;
        @media (max-width: 991px){
            display: flex;
            justify-content: center;
        }
        @media (max-width: 767px) {
            .pagination {
                width: 100%;
            }
        }   

    }
    &.bottom .pagination {
        width: calc(100% - 200px);
        text-align: center;
        @media (max-width: 991px) {
           width: 100%;
        }
        @media (max-width: 767px) {
            padding-bottom: 20px;
            text-align: center;
        }
    }
    @media (max-width: 991px) {
        flex-wrap: wrap;
        
    }
}

.page-type-account_recentitems {
    & .product-pagination.bottom {
        justify-content: flex-end;
        margin-top: 10px;
    }
}

.pagination-list--small{
    text-align: right !important;
    .pagination-item--previous{
        float: none !important;
        margin-right: 25px !important;
    }
    .pagination-item--next{
        margin-left: 25px !important;
    }
    .paginaton-title{
        text-transform: uppercase;
        font-weight: 600;
        margin-right: 25px !important;
    }
}
.pagination-list {
    @include clearfix;
    display: inline-block;
    font-size: 0; // 1
    margin-bottom: 0;
    text-align: center;
    .pagination-item {
        display: inline-block;
        margin: 0;
        cursor: default;
        padding: 0 12px;
        & a {
            display: inline;
            font-size: fontSize("base");
            color: $anchor-font-color;
            font-weight: 600;
            letter-spacing: 0.1em;
            border-radius: 0;
            padding: 5px 0;
            &:hover {
                text-decoration: none;
                color: $anchor-font-color-hover;
            }
        }
        &.pagination-item--previous{
            float: left;
            padding: 0;
            margin-right: 12px;
        }
        &.pagination-item--next {
            float: right;
            padding: 0;      
            margin-left: 12px;      
        }
        &.pagination-item--previous a,
        &.pagination-item--next a {
            color: $anchor-font-color;
            font-family: $fontFamily-headings;
            font-size: fontSize("base");
            text-transform: uppercase;
            font-weight: 600;
            letter-spacing: 0.1em;
            & span {
                border-bottom: 1px dotted $anchor-font-color;
            }
            &:hover {
                color: $anchor-font-color-hover;
                & span {
                    border-bottom-color: $anchor-font-color-hover;
                }
            }
            & i.fa {
                font-size: 14px;
                vertical-align: -1px;
                padding: 0 8px;
            }
        }
        &.pagination-item--current a {
            color: $anchor-font-color;
            border-bottom: 1px dotted $anchor-font-color;
            font-weight: 700;
            margin: 5px;
            padding: 0;
        }
    }
}
.compare-link-top {
    width: 150px;
    text-align: right;
    @media (max-width: 991px) {
        width: auto;
    }
    & a {
        font-family: $fontFamily-headings;
        font-size: 11px;
        font-weight: 700;
        letter-spacing: $headings-letter-spacing;
        text-transform: uppercase;
    }
}
.compare-link {
    align-self: stretch;
    display: inline-flex;
    align-items: center;
    justify-content: flex-end;
    padding: 0;    
    @media(max-width: 991px){
        display: none;
    }
    @media(min-width: 992px){
        position: fixed;
        top: -webkit-calc(50% + 265px);
        top: -moz-calc(50% + 265px);
        top: -ms-calc(50% + 265px);
        top: calc(50% + 265px);
        right: 60px;
        min-width: 80px;
        z-index: 10;
    }
   
     @media (max-width: 400px) {
        width: 100%;
        margin-left: 0;
        text-align: center;
     }
    & a {
        display: none;
        align-items: center;
        justify-content: center;
        padding: 0;
        letter-spacing: $headings-letter-spacing;
        font-size: fontSize("base");
        border:  0 !important;
        height: 32px;
        margin-top: -2px;
        text-decoration: none;

        &.btn-alt {
            min-width: 80px;
            padding: 7px 15px;
            background-color: #fff;
            border: 1px solid #fff;
            border-radius: 3px;
            -webkit-box-shadow: 0px 2px 5px 0px rgba(0, 0, 0, 0.12);
            -moz-box-shadow: 0px 2px 5px 0px rgba(0, 0, 0, 0.12);
            -ms-box-shadow: 0px 2px 5px 0px rgba(0, 0, 0, 0.12);
            box-shadow: 0px 2px 5px 0px rgba(0, 0, 0, 0.12);

            &:hover, &:active, &:focus {
                color: #fff;
                background-color: #000;
                border-color: #000;
            }
        }

        @media (max-width: 400px) {
            width: 100%;
        }
        & .countPill {
            display: none;
        }
    }
    &.link-show {
        a {
            display: flex;
        }
        & .countPill {
            display: inline-block;
        }
    }
}

.compare-link .countPill {
    display: inline-block;
    min-width: 24px;
    margin-left: 10px;
    padding: 0 4px;
    text-align: center;
    border-radius: 50%;
    background-color: #da5b4c;
    color: #fff !important;
    line-height: 24px;
    font-size: 11px;
    font-weight: 700;
}

components/foundation/tabs/tabs.scss:
// =============================================================================
// TABS (CSS)
// =============================================================================
//
// Most of these styles deliberately reset Citadel's defaults for smaller
// screens since the styles in Stencil for smaller screens are more persistent
// across breakpoints.
//
// -----------------------------------------------------------------------------

.tab {
    border-top: $tab-active-border;
    float: none;

    @include breakpoint("medium") {
        border-top-color: $tab-title-border-color;
        float: left;
    }

    &.is-active {
        border-left-color: $tab-title-border-color;
        border-right-color: $tab-title-border-color;

        @include breakpoint("small") {
            margin-bottom: $tab-title-border-bottom-offset;
        }

        @include breakpoint("medium") {
            border: $tab-active-border;
            border-bottom: $tab-title-border;
        }
    }
}

.tab-title {
    padding: spacing("half") + spacing("quarter") 0;
    text-align: center;
    text-decoration: $tab-title-text-decoration;

    @include breakpoint("small") {
        text-align: left;
    }

    @include breakpoint("medium") {
        padding: spacing("half") spacing("single");
    }
}


//
// State for when tab-content has js generated of calculated content, like carousel
//
// Purpose: The content being display: none, means any js calculated dimensions
// are incorrect as the elements inside the tab-content have no dimensions to grab.
// Carousel is a prime example. It needs widths to calculate the layout and slides
// -----------------------------------------------------------------------------

.tab-content.has-jsContent {
    display: block;
    height: 0;
    overflow: hidden;
    padding: 0;
    visibility: hidden;

    &.is-active {
        height: auto;
        overflow: visible;
        padding: $tabs-content-padding;
        visibility: visible;
    }
}

components/foundation/buttons/buttons.sc:
// =============================================================================
// BUTTONS (CSS)
// =============================================================================

/*Buttons*/

.btn {
    font-family: stencilFontFamily('button--font'), Arial, Helvetica, sans-serif;
    font-size: 13px;
    font-weight: stencilFontWeight('button--font');
    height: 42px;
    letter-spacing: 0.1em;
    line-height: initial;
    padding: 9px 25px 8px;
    text-transform: uppercase;
    border-radius: 0px;
    background: #fff;
    text-align: center;
    display: -webkit-inline-flex;
    display: -ms-inline-flexbox;
    display: inline-flex;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    justify-content: center;
}

.btn.btn-sm {
    font-size: 11px;
    line-height: 25px;
    height: 28px;
    padding: 0 15px;
    border-width: 1px !important;
}

.btn.btn-lg {
    line-height: 52px;
    height: 52px;
    padding: 1px 27px;
    font-size: 13px;
}



/*btn-primary*/

.btn.btn-primary {
    color: stencilColor('button--primary-color');
    border: 2px solid stencilColor('button--primary-borderColor');
    background: stencilColor('button--primary-backgroundColor');
}

.btn.btn-primary:hover {
    color: stencilColor('button--primary-colorHover');
    background: stencilColor('button--primary-backgroundColorHover');
    border-color: stencilColor('button--primary-borderColorHover');
}

.btn.btn-primary[disabled],
.btn.btn-primary[disabled]:hover,
.btn.btn-primary[disabled]:focus,
.btn.btn-primary[disabled]:active {
    color: stencilColor('button--primary-color');
    border: 2px solid stencilColor('button--primary-backgroundColor');
    background: stencilColor('button--primary-backgroundColor');
}

.btn-sm.btn-primary {
    line-height: 24px;
}

.btn>i.fa.fa-caret-right {
    font-size: 16px;
    position: relative;
    top: 1px;
    margin-left: 9px;
}


/*btn alternate*/

.btn.btn-alt {
    color: stencilColor('button--alt-color');
    border: 2px solid stencilColor('button--alt-borderColor');
    background: stencilColor('button--alt-bgColor');
}

.btn.btn-alt:hover {
    color: stencilColor('button--alt-colorHover');
    background: stencilColor('button--alt-bgColorHover');
    border-color: stencilColor('button--alt-borderColorHover');
}

.btn.btn-alt-white {
    color: #fff;
    border: 1px solid #fff;
    background: #000;
}

.btn.btn-alt-white:hover {
    color: #000;
    background: #fff;
}

.btn.btn-alt[disabled],
.btn.btn-alt[disabled]:hover,
.btn.btn-alt[disabled]:focus,
.btn.btn-alt[disabled]:active {
    color: stencilColor('button--alt-color');
    border: 2px solid stencilColor('button--alt-borderColor');
    background: stencilColor('button--alt-bgColor');
}



/*btn-default*/

.btn.btn-default {
    border-color: #cbcbcb;
}

.btn.btn-default:hover {
    background: #f5f5f5;
}



/*btn-checkout*/

.btn.btn-checkout {
    max-width: 270px;
    padding-top: 10px;
    color: stencilColor('button--checkout-color');
    border-color: stencilColor('button--checkout-bgColor');
    background: stencilColor('button--checkout-bgColor');
}

.btn.btn-checkout:hover {
    border-color: stencilColor('button--checkout-bgColorHover');
    background: stencilColor('button--checkout-bgColorHover');
}

.btn.btn-checkout.btn-lg {
    padding: 1px 27px;
}


/*btn disabled*/

.btn.disabled,
.btn[disabled],
fieldset[disabled] .btn {
    opacity: .5;
    filter: alpha(opacity=50);
}

.button {
    line-height: 1rem;
    transition: all 0.15s ease;

    +.button {
        margin-left: 0;

        @include breakpoint("medium") {
            margin-left: spacing("half");
        }
    }
}

.button[disabled] {
    cursor: not-allowed;
}

.button--icon {

    svg {
        fill: stencilColor("button--icon-svg-color");
    }
}

components/foundation/accordion/accordio:
// =============================================================================
// ACCORDION (CSS)
// =============================================================================


.accordion {
    border-radius: $accordion-borderRadius;
    box-shadow: none;

    .navList {
        margin-bottom: 0;
    }
}

.accordion-content {
    border: 0;

    .form:only-child {
        margin-bottom: 0;
    }
}

.accordion-title {
    margin: 0;
}

.accordion-navigation {
    margin-top: -1px;
    position: relative;
    text-decoration: none;
}


// Accordion nav list
// -----------------------------------------------------------------------------
//
// Purpose: Used as navigation menu, for example: faceted search menu.
//
// -----------------------------------------------------------------------------
.accordion--navList {
    @include breakpoint("medium") {
        border: 0;
    }

    .sidebarBlock + & {
        @include breakpoint("medium") {
            margin-top: $accordion-navList-spacing;
        }
    }

    .accordion-block {
        @include breakpoint("medium") {
            margin-top: $accordion-navList-spacing;
        }
    }

    .accordion-content {
        padding: $accordion-navList-padding;

        @include breakpoint("medium") {
            padding: 0;
        }
    }

    .accordion-navigation {
        @include breakpoint("medium") {
            background-position: right 0 top 50%;
            border: 0;
        }
    }

    .accordion-title {
        font-weight: normal;
        padding: $accordion-navList-padding;
        text-decoration: none;
        text-transform: inherit;

        @include breakpoint("medium") {
            margin-bottom: spacing("half");
            margin-top: 0;
            padding: 0;
        }
    }

    .accordion-indicator {
        fill: color("greys", "light");

        @include breakpoint("medium") {
            visibility: hidden;
        }
    }

    .accordion-navigation-actions {
        right: $accordion-navList-padding;
        top: $accordion-navList-padding + spacing("eighth");

        @include breakpoint("medium") {
            right: 0;
            top: 0;
        }
    }
}


// Accordion block
// -----------------------------------------------------------------------------
//
// Purpose: Child blocks of an accordion
//
// -----------------------------------------------------------------------------
.accordion-block:first-child {
    margin-top: 0;

    .accordion-navigation {
        border-radius: $accordion-borderRadius $accordion-borderRadius 0 0;
    }
}

.accordion-block:last-child {
    .accordion-navigation,
    .accordion-content {
        border-radius: 0 0 $accordion-borderRadius $accordion-borderRadius;
    }

    .accordion-navigation {
        border-bottom-width: 0;

        // scss-lint:disable NestingDepth
        &.is-open {
            border-bottom-width: 1px;
            border-radius: 0;
        }
    }
}

// Accordion navigation actions
// -----------------------------------------------------------------------------
//
// Purpose: Action links, usually aligned to the right of .accordion-navigation
//
// -----------------------------------------------------------------------------
.accordion-navigation-actions {
    position: absolute;
    right: $accordion-navigation-paddingHorizontal;
    top: $accordion-navigation-paddingVertical;

    > a {
        color: stencilColor("color-textSecondary");
        vertical-align: middle;

        // scss-lint:disable NestingDepth
        &:hover {
            color: stencilColor("color-textSecondary--hover");
        }
        // scss-lint:enable NestingDepth
    }
}

