components/stencil/banners/banners.scss:
// =============================================================================
// Banners Component
// =============================================================================


.fluid-grid {
    display: flex;
    flex-wrap: wrap;
    flex: 1;
    margin-left: -15px;
    margin-right: -15px;
}

.banner-item {
    padding: 0 15px 30px;
    & a {
        position: relative;
        display: block;
        text-align: center;
        overflow: hidden;
        & img:not(.overlay) {
            -moz-transition: 1s ease 0s;
            -webkit-transition: 1s ease 0s;
            transition: 1s ease 0s;
        }
        & img.overlay {
            position: absolute;
            top: 0;
            left: 0;
            transition: none !important;
            transform: none !important;
        }
        &:hover {
            img:not(.overlay) {
                -moz-transform: scale(1.1);
                -webkit-transform: scale(1.1);
                -ms-transform: scale(1.1);
                -o-transform: scale(1.1);
                transform: scale(1.1);
            }
        }
    }

}


/*Home Top Banners*/

#homeTopBanners {
    padding-bottom: 5px;
    & .banner-item {
        width: 50%;
        @media (max-width: 480px) {
            width: 100%;
            max-width: 370px;
            margin-left: auto;
            margin-right: auto;
        }
    }
}


/*Home Bottom Banners*/
#homeBottomBanners {
    padding-bottom: 40px;
    & .banner-item {
        padding: 0 15px 30px;
        @media (max-width: 480px) {
            width: 100%;
            max-width: 370px;
            margin-left: auto;
            margin-right: auto;
        }
        &.wide {
            width: 66.67%;
            @media (max-width: 480px) {
                width: 100%;
                max-width: 370px;
                margin-left: auto;
                margin-right: auto;
            }
        }
    }
}

#instafeed .feed-item .feed-itemImg,
#homeTopCustomBlock .banner-item .banner-item-img > a,
#homeBottomCustomBlock .banner-item .banner-item-img > a {
    position: relative;
    overflow: hidden;
    display: block;

    & img {
        position: absolute;
        top: 50%;
        left: 50%;
        width: auto;
        height: auto;
        -webkit-transform: translate(-50%, -50%);
        -moz-transform: translate(-50%, -50%);
        -ms-transform: translate(-50%, -50%);
        -o-transform: translate(-50%, -50%);
        transform: translate(-50%, -50%);
    }

    &:after {
        content: '';
        display: block;
        width: 100%;
        height: 0;
    }
}

