@mixin modal-header($border-bottom, $hPadding, $vPadding) {
    border-bottom: $border-bottom;
    padding: $vPadding $hPadding;
    position: relative;
}

@mixin modal-body($hPadding, $vPadding) {
    padding: $vPadding $hPadding;
}

@mixin modal-footer($border-bottom, $hPadding, $vPadding, $text-align) {
    border-top: $border-bottom;
    padding: $vPadding $hPadding;
    text-align: center;

    @include breakpoint("xsmall") {
        text-align: $text-align;
    }
}

// =============================================================================
// MODAL (CSS)
// =============================================================================


// Bigcommerce Component
// -----------------------------------------------------------------------------
//
// The Bigcommerce Coding Standards, coupled with Foundation.
// If any library overrides or extra CSS is needed, declare it here.
//
// 1. If set to false, there is no outputted CSS for this component.
//
// -----------------------------------------------------------------------------

@if $exportCSS--modal { // 1

    // Body Element When Modal Is Open
    // -------------------------------------------------------------------------

    body.has-activeModal {
        overflow: hidden;
    }


    // modal Background
    // -------------------------------------------------------------------------

    .modal-background {
        @include reveal-bg; // Foundation mixin

        .has-activeModal & {
            display: block;
        }
    }


    // =============================================================================
    // MODAL BASE
    // =============================================================================

    .#{$reveal-modal-class} {

        // foundation mixins
        @include reveal-modal-base;

        @include reveal-modal-style(
            $bg: $reveal-modal-bg,
            $padding: $reveal-modal-padding,
            $border: true,
            $box-shadow: true,
            $top-offset: $reveal-position-top
        );

        // additional styles
        max-height: $modal-max-height;
        overflow: auto;
        position: fixed;

        @include breakpoint("small") {
            transform: translateY($modal-transformY);
        }
    }

    .modal--large {
        max-height: $modal-max-height--large;
        width: $modal-width--large;
    }

    .modal--small {
        width: $modal-width--small;
    }


    // =============================================================================
    // MODAL HEADER
    // =============================================================================

    .modal-header {
        @include modal-header(
            $modal-border,
            $modal-header-horizontalSpacing,
            $modal-header-verticalSpacing
        );
    }

    .modal-header-title {
        margin: $modal-header-title-margin;
    }


    // =============================================================================
    // MODAL BODY
    // =============================================================================

    .modal-body {
        @include modal-body(
            $modal-body-horizontalSpacing,
            $modal-body-verticalSpacing
        );

        > :last-child {
            margin-bottom: 0;
        }
    }


    // =============================================================================
    // MODAL FOOTER
    // =============================================================================

    .modal-footer {
        @include modal-footer(
            $modal-border,
            $modal-footer-horizontalSpacing,
            $modal-footer-verticalSpacing,
            $modal-footer-align
        );

        .button {
            display: block;

            @include breakpoint("xsmall") {
                display: inline-block;
                margin-bottom: $modal-footer-button-marginBottom;
            }
        }
    }

    .modal-footer-link {
        display: inline-block;
        font-size: $modal-footer-link-fontSize;
        margin: 0 0 spacing("half");

        @include breakpoint("xsmall") {
            display: inline;
            margin: $modal-footer-link-margin;
        }
    }

    .#{$close-reveal-modal-class} {
        @include reveal-close; // Foundation mixin

        transform: translateY($modal-close-transformY);

        .icon {
            @include square($modal-close-size);
        }
    }


    // Print
    //
    // Reveal Print Styles: It should be invisible, adds no value being printed.
    // -------------------------------------------------------------------------
    // scss-lint:disable ImportantRule
    @media print {
        .#{$reveal-modal-class} {
            background: $white !important; // !important from foundation
            display: none;
        }
    }
}

// =============================================================================
// LOADING (Tools)
// =============================================================================


// =============================================================================
// MIXINS FOR THE LOADING NOTIFICATION
// =============================================================================

@mixin loadingNotification-base() {
    left: $loadingNotification-left;
    position: fixed;
    text-align: $loadingNotification-textAlign;
    top: $loadingNotification-top;
    transform: $loadingNotification-transform;
    transform-style: preserve-3d;
    transition: all 200ms ease-out;
    z-index: zIndex("highest");
}

@mixin loadingNotification-startState() {
    opacity: 0;
    top: $loadingNotification-top--startPosition;
    transform: $loadingNotification-transform--start;
}

@mixin loadingNotification-activeState() {
    opacity: 1;
    top: $loadingNotification-top;
    transform: $loadingNotification-transform--end;
}

@mixin loadingNotification-label() {
    background: $loadingNotification-label-background;
    border-radius: $loadingNotification-label-radius;
    color: $loadingNotification-label-color;
    display: inline-block;
    font-size: $loadingNotification-label-fontSize;
    padding: $loadingNotification-label-verticalSpacing $loadingNotification-label-horizontalSpacing;
}


// =============================================================================
// MIXINS FOR THE LOADING OVERLAY
// =============================================================================

@mixin loadingOverlay-container() {
    min-height: $loadingOverlay-minHeight;
    position: relative;
}

@mixin loadingOverlay-base() {
    background: $loadingOverlay-background;
    bottom: 0;
    height: 100%;
    left: 0;
    opacity: 1;
    position: absolute;
    right: 0;
    top: 0;
    transition: $loadingOverlay-transitionProperty $loadingOverlay-transitionSpeed $loadingOverlay-transitionEase;
    width: 100%;
}

@mixin loadingOverlay-spinner() {
    @include circle($loadingOverlay-spinner-size);
    border: solid $loadingOverlay-spinner-borderWidth;
    border-color: $loadingOverlay-spinner-borderColor;
    content: "";
    display: block;
    left: 0;
    margin: 0 auto;
    position: absolute;
    right: 0;
    top: 50%;
    transform: translateY(-50%) rotate(0deg);
    transform-style: preserve-3d;
}

@mixin loadingOverlay-startState() {
    opacity: 0;
}

@mixin loadingOverlay-activeState() {
    opacity: 1;
}

// =============================================================================
// LOADING (CSS)
// =============================================================================


// Bigcommerce Component
// -----------------------------------------------------------------------------
//
// The Bigcommerce Coding Standards, coupled with Foundation.
// If any library overrides or extra CSS is needed, declare it here.
//
// 1. If set to false, there is no outputted CSS for this component.
//
// -----------------------------------------------------------------------------


@if $exportCSS--loading {

    // =============================================================================
    // LOADING NOTIFICATION
    // =============================================================================

    .loadingNotification {
        @include loadingNotification-base();

        &.ng-hide-remove,
        &.ng-hide-add-active {
            @include loadingNotification-startState();
        }

        &.ng-hide-remove-active {
            @include loadingNotification-activeState();
        }
    }

    .loadingNotification-label {
        @include loadingNotification-label();
    }


    // =============================================================================
    // Loading overlay
    // =============================================================================

    .loadingOverlay-container {
        @include loadingOverlay-container();
    }

    .loadingOverlay {
        @include loadingOverlay-base();

        &::before {
            @include loadingOverlay-spinner();
            animation: spin $loadingOverlay-spinner-duration infinite $loadingOverlay-spinner-easing;
        }

        &.ng-enter,
        &.ng-leave.ng-leave-active {
            @include loadingOverlay-startState();
        }

        &.ng-leave,
        &.ng-enter.ng-enter-active {
            @include loadingOverlay-activeState();
        }

    }

    @keyframes spin {
        0% {
            transform: translateY(-50%) rotate(0deg);
        }

        100% {
            transform: translateY(-50%) rotate(360deg);
        }
    }

}

