.heroCarousel {
    margin-bottom: 30px;
    min-height: 100px;
    /*min-width: 320px;*/
    & .owl-nav>div {
        border: 0;
        opacity: 0;
        visibility: hidden;
        @media (max-width: 767px) {
            display: none;
        }
        &::before {
            font-size: 0px;
        }
        &.owl-prev {
            left: 0px;
            background: url(data:image/svg+xml;utf8;base64,);
            background-size: 57px 57px;
            width: 70px;
            height: 70px;
            background-position: center;
            background-repeat: no-repeat;
        }
        &.owl-next {
            right: 0px;
            background: url(data:image/svg+xml;utf8;base64,);
            background-size: 57px 57px;
            width: 70px;
            height: 70px;
            background-position: center;
            background-repeat: no-repeat;
        }
    }
    &:hover .owl-nav>div {
        opacity: 1;
        visibility: visible;
        &.owl-prev {
            left: 30px;
        }
        &.owl-next {
            right: 30px;
        }
    }
    .heroCarousel-image-wrap {
        display: block;
    }
    & .heroCarousel-image {
        width: 100%;
        padding: 0;
        margin: 0 auto;
    }
    & .owl-dots {
        position: absolute;
        bottom: 20px;
        left: 50%;
        -moz-transform: translateX(-50%);
        -webkit-transform: translateX(-50%);
        -ms-transform: translateX(-50%);
        -o-transform: translateX(-50%);
        transform: translateX(-50%);
        @media (max-width: 991px) {
            bottom: -35px;
        }
    }
    & .owl-dots>div {
        position: relative;
        display: inline-block;
        margin: 0 5px !important;
        width: 12px;
        height: 12px;
        /* Dots Puff Style  */
        & span {
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            outline: none;
            border-radius: 50%;
            background-color: #fff;
            background-color: rgba(255, 255, 255, 0.5);
            text-indent: -999em;
            /* make the text accessible to screen readers */
            cursor: pointer;
            position: absolute;
                background: #dad5d5;
            
        }
        & span:focus {
            outline: none;
        }
        & span {
            border: 3px solid transparent;
            -webkit-transition: border-color 0.3s ease;
            transition: border-color 0.3s ease;
        }
        & span::after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            visibility: hidden;
            background: rgba(255, 255, 255, 0.5);
            border-radius: 50%;
            opacity: 0;
            -webkit-transform: scale(3);
            transform: scale(3);
            -webkit-transition: opacity 0.3s ease, -webkit-transform 0.3s ease, visibility 0s 0.3s;
            transition: opacity 0.3s ease, transform 0.3s ease, visibility 0s 0.3s;

        }
        & span:hover,
        & span:focus,
        &.active span {
            border: 1px solid #dad5d5;
            background: #fff;
        }
        &.active span::after {
            visibility: visible;
            opacity: 1;
            -webkit-transform: scale(1);
            transform: scale(1);
            -webkit-transition: opacity 0.3s ease, -webkit-transform 0.3s ease;
            transition: opacity 0.3s ease, transform 0.3s ease;
        }
    }
    @media (max-width: 991px) {
        margin-bottom: 40px;
    }
    @media (max-width: 551px) {
        .heroCarousel-image-wrap {
            position: relative;
            overflow: hidden;
            &:before {
                content: "";
                display: block;
                padding-top: 50%;
            }
            .heroCarousel-image {
                position: absolute;
                height: 100%;
                width: 100%;
                max-width: none;
                left: 50%;
                top: 0;
                bottom: 0;
                transform: translateX(-50%);
                transform-style: preserve-3d;
                display: -ms-flexbox;
                display: -webkit-flex;
                display: flex;
                object-fit: cover;
            }
        }
    }
}

