$secondary-color: #e7e7e7 !default; // white-lilac
$alert-color: #f04124 !default;     // cinnabar
$success-color: #43AC6A !default;   // sea-green
$warning-color: #f08a24 !default;   // carrot
$info-color: #a0d3e8 !default;      // cornflower

// We use these to define default font stacks
$font-family-sans-serif: "Helvetica Neue", Helvetica, Roboto, Arial, sans-serif !default;
$font-family-serif: Georgia, Cambria, "Times New Roman", Times, serif !default;
$font-family-monospace: Consolas, "Liberation Mono", Courier, monospace !default;

// We use these to define default font weights
$font-weight-normal: normal !default;
$font-weight-bold: bold !default;

// We use these to control various global styles
$body-bg: #fff !default;
$body-font-color: #222 !default;
$body-font-family: $font-family-sans-serif !default;
$body-font-weight: $font-weight-normal !default;
$body-font-style: normal !default;

// We use this to control font-smoothing
$font-smoothing: antialiased !default;

// We use these to control text direction settings
$text-direction: ltr !default;
$default-float: left !default;
$opposite-direction: right !default;
@if $text-direction == ltr {
  $default-float: left;
  $opposite-direction: right;
} @else {
  $default-float: right;
  $opposite-direction: left;
}

// We use these to make sure border radius matches unless we want it different.
$global-radius: 3px !default;
$global-rounded: 1000px !default;

// We use these to control inset shadow shiny edges and depressions.
$shiny-edge-size: 0 1px 0 !default;
$shiny-edge-color: rgba(#fff, .5) !default;
$shiny-edge-active-color: rgba(#000, .2) !default;

// We use this to control whether or not CSS classes come through in the gem files.
$include-html-classes: true !default;
$include-print-styles: true !default;
$include-js-meta-styles: true !default; // Warning! Meta styles are a dependancy of the Javascript.
$include-html-global-classes: $include-html-classes !default;

$column-gutter: rem-calc(30) !default;

// Media Query Ranges
$small-breakpoint:  em-calc(640)  !default;
$medium-breakpoint: em-calc(1024) !default;
$large-breakpoint:  em-calc(1440) !default;
$xlarge-breakpoint: em-calc(1920) !default;

$small-range:   (0, $small-breakpoint) !default;
$medium-range:  ($small-breakpoint  + em-calc(1), $medium-breakpoint) !default;
$large-range:   ($medium-breakpoint + em-calc(1), $large-breakpoint)  !default;
$xlarge-range:  ($large-breakpoint  + em-calc(1), $xlarge-breakpoint) !default;
$xxlarge-range: ($xlarge-breakpoint + em-calc(1), em-calc(99999999)) !default;

$screen: "only screen" !default;

$landscape: "#{$screen} and (orientation: landscape)" !default;
$portrait: "#{$screen} and (orientation: portrait)" !default;

$small-up: $screen !default;
$small-only: "#{$screen} and (max-width: #{upper-bound($small-range)})" !default;

$medium-up: "#{$screen} and (min-width:#{lower-bound($medium-range)})" !default;
$medium-only: "#{$screen} and (min-width:#{lower-bound($medium-range)}) and (max-width:#{upper-bound($medium-range)})" !default;

$large-up: "#{$screen} and (min-width:#{lower-bound($large-range)})" !default;
$large-only: "#{$screen} and (min-width:#{lower-bound($large-range)}) and (max-width:#{upper-bound($large-range)})" !default;

$xlarge-up: "#{$screen} and (min-width:#{lower-bound($xlarge-range)})" !default;
$xlarge-only: "#{$screen} and (min-width:#{lower-bound($xlarge-range)}) and (max-width:#{upper-bound($xlarge-range)})" !default;

$xxlarge-up: "#{$screen} and (min-width:#{lower-bound($xxlarge-range)})" !default;
$xxlarge-only: "#{$screen} and (min-width:#{lower-bound($xxlarge-range)}) and (max-width:#{upper-bound($xxlarge-range)})" !default;

$retina: (
  "#{$screen} and (-webkit-min-device-pixel-ratio: 2)",
  "#{$screen} and (min--moz-device-pixel-ratio: 2)",
  "#{$screen} and (-o-min-device-pixel-ratio: 2/1)",
  "#{$screen} and (min-device-pixel-ratio: 2)",
  "#{$screen} and (min-resolution: 192dpi)",
  "#{$screen} and (min-resolution: 2dppx)"
);

// Legacy
$small: $small-up;
$medium: $medium-up;
$large: $large-up;


//We use this as cursors values for enabling the option of having custom cursors in the whole site's stylesheet
$cursor-auto-value: auto !default;
$cursor-crosshair-value: crosshair !default;
$cursor-default-value: default !default;
$cursor-disabled-value: not-allowed !default;
$cursor-pointer-value: pointer !default;
$cursor-help-value: help !default;
$cursor-text-value: text !default;


@include exports("global") {

  // Meta styles are a dependancy of the Javascript.
  // Used to provide media query values for javascript components.
  // Forward slash placed around everything to convince PhantomJS to read the value.

  @if $include-js-meta-styles {

    meta.foundation-version {
      font-family: "/5.5.3/";
    }

    meta.foundation-mq-small {
      font-family: "/" + unquote($small-up) + "/";
      width: lower-bound($small-range);
    }

    meta.foundation-mq-small-only {
      font-family: "/" + unquote($small-only) + "/";
      width: lower-bound($small-range);
    }

    meta.foundation-mq-medium {
      font-family: "/" + unquote($medium-up) + "/";
      width: lower-bound($medium-range);
    }

    meta.foundation-mq-medium-only {
      font-family: "/" + unquote($medium-only) + "/";
      width: lower-bound($medium-range);
    }

    meta.foundation-mq-large {
      font-family: "/" + unquote($large-up) + "/";
      width: lower-bound($large-range);
    }

    meta.foundation-mq-large-only {
      font-family: "/" + unquote($large-only) + "/";
      width: lower-bound($large-range);
    }

    meta.foundation-mq-xlarge {
      font-family: "/" + unquote($xlarge-up) + "/";
      width: lower-bound($xlarge-range);
    }

    meta.foundation-mq-xlarge-only {
      font-family: "/" + unquote($xlarge-only) + "/";
      width: lower-bound($xlarge-range);
    }

    meta.foundation-mq-xxlarge {
      font-family: "/" + unquote($xxlarge-up) + "/";
      width: lower-bound($xxlarge-range);
    }

    meta.foundation-data-attribute-namespace {
      font-family: #{$namespace};
    }

  }

  @if $include-html-global-classes {

    // Must be 100% for off canvas to work
    html, body { height: 100%; }

    // Set box-sizing globally to handle padding and border widths
    *,
    *:before,
    *:after {
      @include box-sizing(border-box);
    }

    html,
    body { font-size: $base-font-size; }

    // Default body styles
    body {
      background: $body-bg;
      color: $body-font-color;
      cursor: $cursor-auto-value;
      font-family: $body-font-family;
      font-style: $body-font-style;
      font-weight: $body-font-weight;
      line-height: $base-line-height; // Set to $base-line-height to take on browser default of 150%
      margin: 0;
      padding: 0;
      position: relative;
    }

  a:hover { cursor: $cursor-pointer-value; }

    // Grid Defaults to get images and embeds to work properly
    img { max-width: 100%; height: auto; }

    img { -ms-interpolation-mode: bicubic; }

    #map_canvas,
    .map_canvas,
    .mqa-display {
      img,
      embed,
      object { max-width: none !important;
      }
    }

    // Miscellaneous useful HTML classes
    .left { float: left !important; }
    .right { float: right !important; }
    .clearfix { @include clearfix; }

    // Hide visually and from screen readers
    .hide {
      display: none;
    }

    // Hide visually and from screen readers, but maintain layout
    .invisible { visibility: hidden; }

    // Font smoothing
    // Antialiased font smoothing works best for light text on a dark background.
    // Apply to single elements instead of globally to body.
    // Note this only applies to webkit-based desktop browsers and Firefox 25 (and later) on the Mac.
    .antialiased { -webkit-font-smoothing: antialiased; -moz-osx-font-smoothing: grayscale; }

    // Get rid of gap under images by making them display: inline-block; by default
    img {
      display: inline-block;
      vertical-align: middle;
    }

    //
    // Global resets for forms
    //

    // Make sure textarea takes on height automatically
    textarea { height: auto; min-height: 50px; }

    // Make select elements 100% width by default
    select { width: 100%; }
  }
}

components/citadel/cards/cards.scss:
// =============================================================================
// CARDS (CSS)
// =============================================================================
//
// 1. Allow users to click the anchor tag on the image in the figcaption empty
//    space. This provides a much larger and simpler hit state for navigating
//    to the product details page. (Does not work in IE10 or earlier).
// 2. Using translate3d to counter a chrome specific bug: When using a masonry
//    styled layout via CSS column-count etc, transforms applied to descendants
//    are made invisible, except for 3d transforms...
//
// -----------------------------------------------------------------------------


.card-figure {
    margin-top: $card-figure-marginTop;
    height: 100%;
    &:hover {

        // scss-lint:disable NestingDepth
        .card-figcaption {
            opacity: 1;
        }
    }
}

.card-figcaption {
    display: none;
    margin: $card-figcaption-margin;
    pointer-events: none; // 1

    @include breakpoint("medium") {
        display: block;
    }
}

.card-figcaption-body {
    display: $card-figcaption-body-display;
    pointer-events: all; // 1
    transform: translate3d(0, -50%, 0); // 2
}

.card-image {
    border: 0;
    display: inline-block;
    width: auto;
}

.card-title {
    font-size: $card-title-fontSize;
    margin: $card-title-margin;

    > a {
        color: $card-title-color;
        text-decoration: $card-title-textDecoration;

        // scss-lint:disable NestingDepth
        &:hover {
            color: $card-title-color-hover;
        }
    }
}

.card-text {
    margin-bottom: $card-text-marginBottom;

    abbr {
        border: 0;
        color: inherit;
        font-size: inherit;
    }
}

.card-figcaption-button {
    border: $card-figcaption-button-border;
    display: $card-figcaption-button-display;
    margin: $card-figcaption-button-margin;

    &:active {
        background-color: $card-figcaption-button-backgroundHover;
        border: $card-figcaption-button-border;
    }

    + .card-figcaption-button {
        margin: $card-figcaption-button-margin;
    }
}

.card-body {
    text-align: center;

    @include breakpoint("small") {
        text-align: left;
    }
}


//
// Alternate card
//
// Purpose: Used to give the card alternative styling
// -----------------------------------------------------------------------------

.card--alternate {
    border: 3px solid stencilColor("card--alternate-borderColor");
    transition: all 0.15s ease;

    .card-body {
        background-color: stencilColor("card--alternate-backgroundColor");
        padding: spacing("half");
        transition: all 0.15s ease;

        @include breakpoint("small") {
            padding: spacing("single");
        }
    }

    &:hover {
        border-color: stencilColor("color-primary");

        // scss-lint:disable SelectorDepth, NestingDepth
        .card-body {
            background-color: stencilColor("color-primary");

            .card-text {
                color: stencilColor("card--alternate-color--hover");
            }
        }

        .card-title > a {
            color: stencilColor("card--alternate-color--hover");
        }
    }
}


.card-img-container {
    position: relative;
    img {
        transition: all 0.5s ease;
    }
    .replace-2x,
    .product-additional {
        top: 0;
        left: 0;
        width: 100%;
        opacity: 0;
        z-index: 5;
        position: absolute;
        visibility: hidden;
        filter: alpha(opacity=0);
        transition: all .6s ease;
    }
}
.card {
    @media (min-width: 1025px) {
        &:hover {
            .card-img-container {
                .replace-2x,
                .product-additional {
                    opacity: 1;
                    visibility: visible;
                    filter: alpha(opacity=100);
                }
            }
        }
    }
}
.card .card-option {
    .form-option {
        width: 22px;
        height: 22px;
        margin: 0 5px 0 0;
        border-radius: 50%;
        position: relative;
        padding: 1px;
        cursor: unset;

        .form-option-tooltip {
            display: none;
            font-size: 1rem;
            font-weight: 400;
            letter-spacing: 0;
            line-height: 1;
            color: #fff;
            text-align: center;
            background: #000000;
            position: absolute;
            bottom: 100%;
            left: 50%;
            padding: 4px 7px;
            z-index: 2;
            min-width: 40px;
            transform: translateX(-50%);
            margin: 0 0 10px 0;
            white-space: nowrap;

            &:before {
                content: " ";
                position: absolute;
                bottom: -6px;
                left: 50%;
                height: 0;
                width: 0;
                border-top: 6px solid #000000;
                border-left: 6px solid transparent;
                border-right: 6px solid transparent;
                transform: translateX(-50%);
            }
        }

        &:hover {
            border-color: #dedede;

            .form-option-tooltip {
                display: block;
            }
        }

        .form-option-variant {
            width: 100%;
            height: 100%;
            border-radius: 50%;
            background-size: cover;
        }
    }
    .showmore {
        width: 22px;
        height: 22px;
        line-height: 22px;
        margin: 0;
        display: inline-block;
        vertical-align: top;
    }
}
/* Start Quick Shop */
.card .card-figure {
    &.has-popup {
        .quickShopPopup {
            display: -webkit-box; 
            display: -moz-box;
            display: -ms-flexbox;
            display: -webkit-flex; 
            display: flex;
        }
        .prod-image .actions {
            display: none;
        }
    }
}
.quickShopPopup {
    position: absolute;
    z-index: 10;
    top: 50%;
    transition: all 0.3s ease;
    transform: translateY(-50%);
    width: -webkit-calc(100% - 20px);
    width: -moz-calc(100% - 20px);
    width: -ms-calc(100% - 20px);
    width: calc(100% - 20px);
    max-height: -webkit-calc(100% - 20px);
    max-height: -moz-calc(100% - 20px);
    max-height: -ms-calc(100% - 20px);
    max-height: calc(100% - 20px);
    height: auto;
    margin: auto;
    min-height: 100px;
    display: none;
    background-color: #fff;

    .form-action-block {
        position: absolute;
        bottom: 0;
        left: 0;
        right: 0;
        padding: 15px;
        background-color: #fff;
        font-size: 0;
    }

    .themevale_close {
        width: 50%;
        display: inline-block;
        padding: 0 0 0 5px;
        vertical-align: top;
        .icon-close {
            display: block;
            padding: 5px 10px;
            text-transform: uppercase;
            font-size: 1rem;
            font-weight: 500;
            border: 1px solid #000;
            height: 32px;
            line-height: 20px;
        }
    }
    .loadingOverlay {
        background-color: rgba(255, 255, 255, 0);
    }
    .quickShopWrap {
        width: 100%;
        height: auto;
        position: relative;
    }
    .form {
        width: 100%;    
    }
    .productView {
        padding-top: 0;
        width: 100%;
        height: 100%;
        padding-bottom: 62px;
        line-height: 1;
    }
    .quickShopInner {
        width: 100%;
        padding: 15px 15px 10px 15px;
        display: inline-block;
        position: relative;
        overflow-x: hidden;
        overflow-y: auto;
        height: 100%;

        [data-product-option-change2] .form-field {
            overflow: hidden;
        }

        @include breakpoint('medium') {
            scrollbar-color: #323232 #fff;
            scrollbar-width: thin !important;
            &::-webkit-scrollbar {
              width: 5px;
              height: 5px;
              background-color: #fff;
            }
            &::-webkit-scrollbar-thumb {
              background-color: #323232;
            }
        }

        .form-option-expanded {
            .form-option-image {
                width: 80px;
                height: 80px;
            }
        }
        .productView-price {
            display: none;
            .price-section {
                font-size: 12px;
                color: stencilColor('card-price-color');
                &.non-sale-price--withoutTax, 
                &.non-sale-price--withTax,
                &.rrp-price--withoutTax,
                &.rrp-price--withTax,
                &.price-section--saving {
                    display: none !important; 
                }
            }
        }
        .quickShop-title {
            margin-bottom: 15px;
            span {
                display: inline-block;
                font-size: 12px;
                color: #787878;
                border-bottom: 1px solid #e4e4e4;
                letter-spacing: 0.02em;
                line-height: 1;
            }
        }
    }

    .card-price {
        position: relative;
        text-align: center;
        margin-top: 12px;
        margin-bottom: 50px;
        -ms-flex-pack: center;
        -webkit-justify-content: center;
        justify-content: center;

        .page-type-default &,
        .page-type-product &{
            margin-bottom: 0;
        }
        .price-section {
            color: stencilColor('card-price-color');
            &.price-section--saving {
                display: none !important;
            }
            &.rrp-price--withoutTax , &.rrp-price--withTax  {
                display: none !important;
                + .non-sale-price--withoutTax , + .non-sale-price--withoutTax{
                    display: none !important;
                    &.no-retail-price {
                        display: none !important;
                    }
                }
            }  
            &.price-section--withoutTax,
            &.price-section--withTax {
                color: stencilColor('card-price-color');
            }         
        }
    }
    .productView-options {
        text-align: center;
        height: 100%;
        margin-bottom: 0;

        .form {
            height: 100%;
            display: inline-block;
            margin-bottom: 0;
        }
        .form-field {
            line-height: 1;
            margin-bottom: remCalc(5px);

            &[data-product-attribute="swatch"],
            &[data-product-attribute="set-rectangle"] {
                margin-bottom: remCalc(5px);
            }

            @include breakpoint("medium") {
                margin-bottom: remCalc(20px);
                &[data-product-attribute="swatch"],
                &[data-product-attribute="set-rectangle"] {
                    margin-bottom: remCalc(20px);
                }
            }
            &:last-child {
                margin-bottom: 0;
            }
            &[data-product-attribute="swatch"] {
                .form-option {
                    width: 34px;
                    height: 34px;
                    border-radius: 50%;
                    padding: 2px;
                    margin: 0 5px 10px;
                    overflow: visible;

                    .form-option-expanded {
                        left: calc(100% - 13px);
                        @media (max-width: 1024px) {
                            display: none;
                        }
                    }
                    .form-option-variant {
                        width: 100%;
                        height: 100%;
                        border-radius: 50%;
                    }
                }
                .form-radio:checked + .form-option > span {
                    box-shadow: none;
                }
            }
            &[data-product-attribute="set-rectangle"] {

                .form-option {
                    border: none;
                    height: auto;
                    min-width: 20px;
                    min-height: 9px;

                    > span {
                        padding: 0;
                        line-height: 1.2;
                        border-bottom: 1px solid transparent;
                    }
                }

                .form-radio:checked + .form-option > span {
                    box-shadow: none;
                    border-color: #000;
                }
            }
            > .form-label--alternate {
                display: block;
                font-size: 12px;
                color: #787878;
                font-weight: 400;
                letter-spacing: 0.02em;
            }
        }
        .form-label {
            margin-bottom: remCalc(8px);
            font-size: 12px;

            @include breakpoint("medium") {
                font-size: 14px;
            }
        }

        .form-field--increments {
            margin-top: 15px;
        }

        .form-action {
            &.addToCart {
                width: 50%;
                margin: 0;
                max-width: 100%;
                padding: 0 5px 0 0;
                display: inline-block;
                vertical-align: top;
                .button {
                    font-size: 1rem;
                    text-transform: uppercase;
                    font-weight: 500;
                    line-height: 20px;
                    width: 100%;
                    margin: 0;
                    padding: 5px 10px;
                    color: #fff;
                    border: 1px solid #000;
                    background-color: #000;
                }
            }
        }
    }

    @media(max-width: 1199px) {
        width: -webkit-calc(100% - 30px);
        width: -moz-calc(100% - 30px);
        width: -ms-calc(100% - 30px);
        width: calc(100% - 30px);
        height: auto;
        max-width: 500px;
        max-height: -webkit-calc(100% - 30px);
        max-height: -moz-calc(100% - 30px);
        max-height: -ms-calc(100% - 30px);
        max-height: calc(100% - 30px);
        position: fixed;
        top: 50%;
        left: 0;
        right: 0;
        z-index: 1005;
    }
}
.module-wrapper.productList .productBlockContainer .prod-item {
    .quickShopPopup {
        position: static;
        width: 100%;
        height: auto;
        min-height: 0;
        background-color: transparent;
        transform: unset;
        display: block;
        max-width: 100%;

        .productView {
            max-height: 100%;
            overflow: visible;
            margin: 15px 0 25px;
            padding-bottom: 0;

            .addToCart {
                display: none;
            }
            .themevale_close {
                display: none;
            }
            .form-action-block {
                display: none;
            }
            .quickShop-title {
                display: none;
            }
            .productView-options {
                text-align: left;

                .quickShopInner {
                    padding: 0;
                    overflow: visible;
                }
            }
        }
    }
    .card-option {
        display: none;
    }
    .actions {
        display: block;
    }
}
.card.has-popup {
    position: static !important;
}
.card-figure .prod-image .quickShopPopup .productView-options .form-field--increments .form-increment {
    .button {
        width: 32px;
        height: 32px;
    }
    .form-input--incrementTotal {
        height: 32px;
    }
}

@media(max-width: 1199px) {
    .modal-quickShopPopup .quickShopPopup {
        display: -webkit-box; 
        display: -moz-box;
        display: -ms-flexbox;
        display: -webkit-flex; 
        display: flex;
    }
}
/* End Quick Shop */

#sidebarFeatured .prod-item .card {
    .btnQV,
    .btnWL {
        display: none;
    }
}

components/stencil/cart/cart.scss:
// =============================================================================
// CART (CSS)
// =============================================================================
//
// 1. Override server-generated inline-css
//
// -----------------------------------------------------------------------------
// Cart variables
// -----------------------------------------------------------------------------
$cart-item-spacing: spacing('single');
$cart-thumbnail-maxWidth: remCalc(100px);
$cart-thumbnail-height: remCalc(100px);
$cart-item-label-offset: $cart-thumbnail-maxWidth+$cart-item-spacing;
// Cart layout
// -----------------------------------------------------------------------------
//
// 1. On mobile and tablet we need to re-structure the table to appear more like
//    a list, then visually show it as a table on larger screens only.
//
// 2. Hide the table headings on mobile and tablet, show them for larger screens.
//
// -----------------------------------------------------------------------------
[data-cart-page-title] {
    span {
        font-weight: 400;
        color: #a0a0a0;
        font-size: 18px;
        letter-spacing: 0.05em;
    }
}

.cart {
    display: block; // 1
    width: 100%;
    @include breakpoint('medium') {
        display: table; // 1
    }
}

.cart-header {
    display: none; // 2
    @include breakpoint('medium') {
        display: table-header-group; // 2
        text-align: left;
    }
}

.cart-header-item {
    font-family: $fontFamily-headings;
    font-size: fontSize("base");
    font-weight: 700;
    min-height: 41px;
    padding: 10px 0 12px;
    letter-spacing: 0.15em;
    text-transform: uppercase;
    color: #000000;
    border-bottom: 3px solid #000;
    &:last-child {
        text-align: right;
    }
}

.cart-list {
    display: block; // 1
    border-top: container('border');
    @include breakpoint('medium') {
        display: table-row-group; // 1
    }
}

.cart-item {
    display: block; // 1
    clear: both;
    padding: $cart-item-spacing 0;
    position: relative;
    @include breakpoint('small') {
        padding-left: 0;
        @include clearfix;
    }
    @include breakpoint('medium') {
        display: table-row; // 1
        padding-left: 0;
        &:before,
        &:after {
            display: none;
        }
    }
    border-bottom: 1px solid #e5e5e5;
    &:last-of-type {
        border: 0;
    }
    @media (max-width: 767px) {
        border-bottom: 1px solid #e5e5e5;
        overflow: hidden;
    }
}

.cart-item-block {
    display: block; // 1
    @include breakpoint('medium') {
        display: table-cell; // 1
        padding: 40px 30px;
        vertical-align: top;
    }
    .definitionList {
        margin-bottom: spacing('quarter');
    }
}

.cart-item-figure {
    float: left;
    width: grid-calc(4, $total-columns);
    height: $cart-thumbnail-height;
    margin-bottom: $cart-item-spacing;
    padding-right: 0;
    padding-left: 0;
    text-align: right;
    @include breakpoint('small') {
        // height: auto;
        width: grid-calc(4, $total-columns);
    }
    @include breakpoint('medium') {
        float: none;
        width: grid-calc(1.75, $total-columns);
        text-align: left;
    }
}

.cart-item-fixed-image {
    width: 100%;
}

.cart-item-image {
    width: auto;
    @media (max-width: 767px) {
        border: 0;
    }
}

.cart-item-title {
    padding-top: 48px;
    display: table-cell;
    padding-left: 30px;
    @include breakpoint('small') {
        display: block;
        overflow: hidden;
        height: auto;
        margin-bottom: $cart-item-spacing;
    }
    @include breakpoint('medium') {
        display: table-cell;
    }
    a[data-item-edit] {
        font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
        font-size: fontSize("base");
        font-weight: 600;
        color: #e95144;
        &:hover {
            color: $anchor-font-color;
            border-bottom: 1px dotted $anchor-font-color;
        }
    }
    @media (max-width: 767px) {
        padding-top: 0;
    }
}

.cart-item-info {
    clear: both;
    width: 100%;
    @include breakpoint('small') {
        float: left;
        clear: none;
        // overflow: hidden;
        width: grid-calc(4, $total-columns);
        +.cart-item-info {
            margin-top: 0;
        }
    }
    @include breakpoint('medium') {
        float: none;
        width: grid-calc(2, $total-columns);
        &:last-child {
            text-align: right;
        }
    }
    +.cart-item-info {
        margin-top: spacing('half');
        padding-right: 60px;
    }
    &:not(.cart-item-quantity) {
        font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
        font-size: 14px;
        padding-top: 60px;
        & strong {
            //font-weight: 400;
        }
        &:last-of-type {
            & strong {
                font-weight: 700;
            }
        }
    }
}

@media (min-width: 992px) {
    .cart-item-info.cart-item-quantity {
        width: 20%;
    }
}

@media (max-width: 991px) {
    .cart-item-info.cart-item-quantity {
        width: 25%;
        padding-left: 0;
        padding-right: 0;
    }
}

@media (max-width: 767px) {
    .cart-item-figure {
        height: auto;
    }
    .cart-item-info {
        display: block;
        width: 100% !important;
        padding-top: 5px !important;
        text-align: right;
        & .cart-item-label {
            font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
            font-size: 14px;
            font-weight: 400;
            float: left;
            width: 33.33%;
            text-align: left;
        }
        &.cart-item-quantity {
            & .cart-item-label {
                padding-top: 7px;
            }
        }
    }
}

// Cart content
// -----------------------------------------------------------------------------
.cart-header-itemprice,
.cart-item-itemprice {
    @include breakpoint('medium') {
        text-align: center;
    }
}

.cart-header-quantity,
.cart-item-quantity {
    @include breakpoint('medium') {
        text-align: center;
    }
}

.cart-item-quantity {
    .form-increment {
        display: flex;
        justify-content: center;
        margin: 0 spacing('eighth') 1px spacing('single');
        vertical-align: middle;
        @include breakpoint('medium') {
            margin-left: 0;
            margin-top: 10px;
        }
        @media (max-width: 767px) {
            justify-content: flex-end;
            margin: 0;
        }
        button[data-action='dec'] {
            // margin-right: -3px;
        }
        input.form-input--incrementTotal {
            width: 76px;
        }
        button[data-action='inc'] {
            // margin-left: -3px;
        }
    }
}

.cart-item-label {
    display: block;
    float: left;
    width: grid-calc(4, $total-columns);
    width: 100%;
    margin-bottom: spacing('quarter');
    text-align: right;
    text-align: left;
    color: #000;
    @include breakpoint('medium') {
        display: none;
    }
}

.cart-item-value {
    padding-left: $cart-item-spacing;
    @include breakpoint('medium') {
        padding-left: 0;
    }
    &.price--discounted {
        margin-right: 5px;
    }
}

.cart-item-brand {
    line-height: 1em;
    margin-bottom: 10px;
    min-height: 12px;
    font-size: 13px;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: stencilColor('basic-price-color');
    font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
    font-weight: 700;
}

.cart-item-name {
    font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
    font-size: fontSize("base");
    font-weight: stencilFontWeight('card-title-font');
    margin: 0 0 14px;
    letter-spacing: 0;
    & a {
        line-height: 1.5em;
    }
    +.definitionList {
        margin-top: spacing('quarter');
    }
}

.cart-item-options--giftWrapping {
    margin-top: spacing('half') + spacing('quarter');
    & .cart-item-option-title {
        & i.icon-gift-wrap {
            display: inline-block;
            width: 13px;
            height: 15px;
            background: url('../img/GiftWrapIcon.png') center no-repeat;
            margin-right: 10px;
            position: relative;
            top: 1px;
        }
        &>span {
            font-weight: 600;
        }
        &>a {
            font-weight: 600;
            color: $anchor-font-color-hover;
            border-bottom: 1px dotted;
            margin-left: 8px;
            &:hover {
                color: $anchor-font-color;
            }
        }
    }

    .cart-item-option-description {
        color: $body-font-color;
        & a {
            font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
            font-size: 13px;
            font-style: italic;
            color: $anchor-font-color-hover;
            &:hover {
                color: $anchor-font-color;
                border-bottom: 1px solid $anchor-font-color;
            }
        }
        & .cart-item-option-actions {
            margin-left: 10px;
        }
    }
}

.cart-remove-button {
    display: block;
    clear: both;
    padding: 11px 0 0;
    position: relative;
    right: -60px;
    top: -37px;
    @media(max-width: 767px){
        top: -42px;
    }
}

.cart-remove {
    width: 32px;
    height: 32px;
    padding: spacing('eighth');
    padding: 0;
    cursor: pointer;
    transition: background-color .15s ease;
    text-align: center;
    border: 1px solid #f14d48;
    display: inline-flex;
    justify-content: center;
    align-items: center;
    &.cart-remove--spacer {
        visibility: hidden;
    }
    & svg {
        position: relative;
        top: -1px;
        width: 19px;
        height: 19px;
        vertical-align: middle;
        fill: #fff;
        border-radius: 1px;
        padding: 0px 1px;
    }
    .fa{
       color: #f14d48;
    }
    & span {
        font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
        font-weight: 500;
        margin-left: 10px;
        letter-spacing: .02em;
        color: #f14d48;
    }
    &:hover {
        & span {
            border-bottom: 1px solid #f14d48;
        }
    }
}

// Cart totals
// -----------------------------------------------------------------------------
[data-cart-totals] {
    overflow: hidden;
    padding: 10px 0px 5px;
    border-top: 1px solid #e8e8e8;
    @media (max-width: 767px) {
        padding-left: 0;
        padding-right: 0;
    }
}

.cart-totals {
    float: right;
    width: 100%;
    width: grid-calc(6, $total-columns);
    margin-bottom: 0;
    text-align: right;
    @include u-listBullets('none');
    @include breakpoint('large') {
        width: grid-calc(6, $total-columns);
    }
    @media (max-width: 767px) {
        width: 100%;
    }
}

.cart-total {
    border-bottom: 0;
    @include clearfix;
    +.cart-total {
        border-top: container('border');
    }
    .form-input {
        display: inline-block;
        width: grid-calc(9, $total-columns);
        vertical-align: top;
    }
    .form-select {
        margin-bottom: spacing('half');
        padding: spacing('third') spacing('half');
    }
    .form-inlineMessage {
        text-align: left;
    }
    &:first-child .cart-total-value {
        font-size: remCalc(16px);
        font-weight: 700;
    }
}

.cart-total-label {
    display: block;
    width: grid-calc(4, $total-columns);
    width: grid-calc(6, $total-columns);
    padding-left: 0;
    text-align: left;
}

.cart-total-value {
    width: grid-calc(6, $total-columns);
    text-align: right;
}

.cart-total-label,
.cart-total-value {
    font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
    font-size: 14px;
    float: left;
    padding: 24px 0;
    color: #000;
}

.cart-total-grandTotal {
    font-size: remCalc(22px);
    font-weight: 700;
    position: relative;
    @include breakpoint('medium') {
        padding: spacing('base') 0;
    }
}

.cart-total:last-child>.cart-total-label {
    font-size: remCalc(18px);
    letter-spacing: 0.02em;
}

.cart-total:last-child>div {
    padding-top: 20px;
}



// Cart actions
// -----------------------------------------------------------------------------
.coupon-code-add,
.coupon-code-cancel,
.shipping-estimate-show,
.shipping-estimate-hide,
.gift-certificate-add,
.gift-certificate-cancel {
    padding: 0;
    transition: color .15s ease;
    color: #a0a0a0;
    font-weight: 600;
    font-size: fontSize("base");
    letter-spacing: 0.05em;
    &:hover {
        text-decoration: none;
        & span {
            color: #e95144;
        }
    }
}

.shipping-estimate-show .shipping-estimate-value {
    letter-spacing: 0;
}

.coupon-code-cancel,
.shipping-estimate-hide {
    font-weight: 600;
    font-size: fontSize("base");
    color: #e95144;
}

.coupon-form,
.cart-gift-certificate-form {
    font-size: 0;
    margin-bottom: spacing('base');
    padding-bottom: 18px;
    @include clearfix;
    .form-input,
    .btn {
        float: left;
    }
    .form-input {
        width: 78%;
        padding-top: 10px;
        @media (max-width: 320px) {
            width: 100%;
        }
    }
    .btn {
        width: 20%;
        margin: 0 0 0 2%;
        padding-right: spacing('half');
        padding-left: spacing('half');
        @media (max-width: 320px) {
            width: 100%;
            margin: 10px 0 0 0;
        }
    }
}

.estimator-form {
    margin-bottom: 20px;
    .form-input {
        width: 100%;
        margin-bottom: spacing('half');
    }
    .btn-primary {
        width: 70%;
        @media (max-width: 320px) {
            width: 100%;
        }
    }
    &~.shipping-quotes {
        padding-bottom: 20px;
    }
}

.estimator-form-label {
    font-weight: fontWeight('normal');
    float: left;
    clear: both;
    margin-bottom: 0;
    min-height: 40px;
    display: inline-flex;
    align-items: center;
    .form-label:not(.estimator-form-label-text) {
        font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
        font-size: fontSize("base");
        line-height: remCalc(32px);
        margin-top: 2px;
        margin-bottom: 0;
    }
}

.estimator-form-input {
    float: right;
    width: 70%;
    margin-bottom: 1em;
    .form-input,
    .form-select {
        margin-bottom: 0;
    }
    @media (max-width: 320px) {
        width: 100%;
        float: none;
        clear: both;
    }
}

.estimator-form-input--price {
    width: 30%;
}

.estimator-form-row {
    @include clearfix;
}

.estimator-form-toggleUPSRate {
    display: block;
    margin-bottom: 1em;
}

.shipping-quotes .btn.select-shipping-quote {
    margin-bottom: 15px;
}

.cart-actions {
    clear: both;
    @include clearfix;
    width: 100%;
    max-width: 50%;
    float: right;
    @media (max-width: 767px) {
        padding-top: 0;
        max-width: 60%;
    }
    @media (max-width: 480px) {
        max-width: 100%;
    }
    .btn-lg {
        font-size: 13px;
        width: 100%;
        max-width: 100%;
        @media (max-width: 480px) {
            max-width: 100%;
        }
    }
    .checkoutMultiple {
        display: inline-block;
        float: none;
        clear: right;
        padding-top: 15px;
        margin-bottom: 5px;
        &>span {
            border-bottom: 1px dotted #000;
        }
        &:hover>span {
            border-bottom-color: $anchor-font-color-hover;
        }
    }
}

.cart-additionalCheckoutButtons {
    text-align: right;
    clear: both; // 1
    @include clearfix; // scss-lint:disable SelectorFormat
    .FloatRight {
        @include clearfix; // scss-lint:disable SelectorDepth, NestingDepth
        p {
            // scss-lint:disable ImportantRule
            float: none !important;
            margin: spacing('third') 0; //text-align: right;
            margin-bottom: 10px;
        }
        div {
            //float: right;
        }
        
    }
    &>div {
        display: inline-block;
        width: 100%;
        text-align: center;
        max-width: 50%;
        @media (max-width: 767px) {
            max-width: 60%;
        }
        @media (max-width: 480px) {
            max-width: 100%;
        }
    }
    // .CheckoutButton {
    //     margin-bottom: spacing("base");

    //     &:first-child {
    //         margin-top: spacing("single");
    //     }

    //     &:last-child {
    //         margin-bottom: spacing("single");
    //     }
    // }
}

// Cart Preview
// -----------------------------------------------------------------------------
//
// 1. Need to vertically align a block of text
//
// -----------------------------------------------------------------------------
.triangle-with-shadow {
    position: absolute;
    top: -15px;
    left: 0;
    overflow: hidden;
    width: 100%;
    height: 15px;
}

.triangle-with-shadow::before {
    position: absolute;
    top: 11px;
    left: 50%;
    width: 16px;
    height: 16px;
    margin-left: -8px;
    content: '';
    -webkit-transform: rotate(45deg);
    -moz-transform: rotate(45deg);
    -ms-transform: rotate(45deg);
    -o-transform: rotate(45deg);
    transform: rotate(45deg);
    border: 1px solid #d0d0d0;
    background: #fff;
    -webkit-box-shadow: 0 0 2px 0 rgba(0, 0, 0, .2);
    -moz-box-shadow: 0 0 2px 0 rgba(0, 0, 0, .2);
    box-shadow: 0 0 2px 0 rgba(0, 0, 0, .2);
}

.triangle-with-shadow.triangle-grey::before {
    background: #f5f5f5;
    @media (max-width: 480px) {
        background: #fff;
    }
}

#cart-preview-dropdown {
    position: fixed;
    z-index: 1000;
    top: 0;
    right: -101%;
    left: auto;
    bottom: 0;
    width: 470px;
    min-height: 81px;
    padding: 0;
    border: 1px solid #eaeaea;
    border-radius: 0;
    background: rgb(255, 255, 255);
    -webkit-box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
    -moz-box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
    box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
    -webkit-transition: all 0.3s ease;
    -moz-transition: all 0.3s ease;
    -o-transition: all 0.3s ease;
    transition: all 0.3s ease;
    @media (max-width: 767px) {
        max-width: 380px;
        width: -webkit-calc(100% - 41px);
        width: -moz-calc(100% - 41px);
        width: -ms-calc(100% - 41px);
        width: calc(100% - 41px);
    }
    & .triangle-with-shadow {
        margin-left: -28px;
        display: none;
        @media (max-width: 991px) {
            margin-left: -8px;
        }
    }
    & .triangle-with-shadow::before {
        right: 53px;
        left: auto;
        margin: 0;

        @media (max-width: 991px) {
            right: 15px;
        }
    }
    & .cart-preview-inner {
        overflow: auto;
        width: 100%;
        height: 100%;
        border-radius: 0;
        
    }
    & .cart-total-label {
        width: 67%;
        color: #000;
        letter-spacing: 0.05em;
        font-size: fontSize("base");
        @media (max-width: 480px) {
            text-align: left;
        }
    }
    & .cart-total-value {
        font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
        width: 33%;
        text-align: right;
        font-weight: 700;
    }
    & .preview-cart-grand-total .cart-total-value {
        font-size: 18px;
    }
    .preview-cart-titles {
        min-height: 32px;
        padding: 11px 30px 12px;
        background: #f5f5f5;
        &>div {
            font-family: $fontFamily-headings;
            font-size: 11px;
            font-weight: 600;
            float: left;
            letter-spacing: 0.1em;
            text-transform: uppercase;
            color: #8d8d8d;
            &.item-info {
                width: 50%;
                text-align: left;
            }
        }
        > h3 {
            color: #8D8D8D;
            text-transform: uppercase;
            letter-spacing: .02em;
            font-size: 12px;
            font-weight: 600;
            margin: 0;
            text-align: left;
        }
        @media(max-width: 767px) {
            padding: 11px 20px 12px;
        }
    }
    & .cart-additionalCheckoutButtons>div {
        text-align: center;
        display: block;
        max-width: 100%;
        margin: 0;
    }
    @media (max-width: 480px) {
        & .previewCartAction-checkoutMultiple {
            width: 100%;
            text-align: center;
            max-width: 100%;
        }
        & .cart-additionalCheckoutButtons>div {
            text-align: center;
        }
    }
}
.previewCart-header {
    .free_shipping_message {
        padding: 10px 30px 0;
        .fsm-content {
            margin-bottom: 0 !important;
        }
    }
    .preview-cart-titles {
        position: relative;
    }
    .previewCart-icon-close {
        position: absolute;
        top: 50%;
        -webkit-transform: translateY(-50%);
        -moz-transform: translateY(-50%);
        -ms-transform: translateY(-50%);
        transform: translateY(-50%);
        right: 30px;
        cursor: pointer;
        width: 17px;
        height: 17px;
        svg {
            fill: #8d8d8d;
            stroke: transparent;
            width: 100%;
            height: 100%;
        }
    }
    @media(max-width: 767px) {
        .previewCart-icon-close {
            right: 20px;
        }
    }
}
.qty-group {
    .cart-item-label {
        display: block;
        margin-bottom: 8px;
    }
    .form-increment {
        font-size: 0;
        display: inline-block;
    }
    .previewCart-removeItem {
        display: inline-block;
        vertical-align: middle;
        margin-left: 26px;
        .btn-removeItem {
            display: inline-block;
            font-size: 12px;
            font-weight: 400;
            letter-spacing: 0.02em;
            color: #FF7677;
            border-bottom: 1px solid #FF7677;
        }
        @media(max-width: 767px) {
            display: block;
            text-align: left;
            margin: 15px 0 0;
        }
    }
}
.previewCartItem-option {
    margin-bottom: 10px;
    .btn-editPreCart {
        margin-left: 7px;
        display: inline-block;
        vertical-align: text-bottom;
    }
}
body.side-cart-open {
    overflow: hidden;
    #cart-preview-dropdown {
        right: 0;
    }
    &:after {
        content: "";
        position: absolute;
        z-index: 102;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        width: 100%;
        height: 100%;
        background: rgba(0, 0, 0, 0.55);
        -webkit-transition: opacity 0.5s ease 0s, width 0.1s ease 0.5s, height 0.1s ease 0.5s;
        -moz-transition: opacity 0.5s ease 0s, width 0.1s ease 0.5s, height 0.1s ease 0.5s;
        -ms-transition: opacity 0.5s ease 0s, width 0.1s ease 0.5s, height 0.1s ease 0.5s;
        transition: opacity 0.5s ease 0s, width 0.1s ease 0.5s, height 0.1s ease 0.5s;
    }
}
@media(min-width: 1450px) {
    body.is-sticky #cart-preview-dropdown {
        top: 54px;
    }
}
@media(min-width: 1025px) and (max-width: 1449px) {
