                    }
                }
            }

            &.cart-edit-quantity {
                min-width: 164px;
            }
            &.cart-edit-option {
                min-width: 170px;
            }
        }
    }

    .form{
        margin: 0;
    }

    .form-increment{
        display: inline-block;
        vertical-align: middle;
        position: relative;
        font-size: 0;
        .button {
            vertical-align: middle;
            &.dec {
                &:before {
                    border-top-color: #000000;
                    bottom: 1px;
                }
            }
            &.inc {
                &:before {
                    border-bottom-color: #000000;
                    top: 1px;
                }
            }
        }
        .form-input--incrementTotal{
            min-width: 70px;
            padding: 0 10px;
            font-size: $fontSize-root;
            font-weight: 400;
        }
    }

    .cart-edit-price{
        .price{
            display: block;
            min-width: 80px;
            font-weight: 600;
            color: #000;
        }
    }

    .cart-edit-remove{
        a {
            color: #BBBBBB;
            font-size: 28px;
            margin: 0;
            position: relative;
            top: 7px;
            line-height: 20px;
            width: 17px;
            height: 17px;
            font-weight: 200;
            display: inline-block;
        }
    }

    .product-edit-change{
        &:nth-child(1){
            thead{
                display: table-header-group;
            }

            .cart-edit-remove{
                a{
                    opacity: 0;
                    visibility: hidden;
                    pointer-events: none;
                }
            }
        }
    }
}

.product-edit-action{
    .product-add-more{
        margin: 20px 0 0 0;
        padding: 0 20px 20px 20px;
        @include breakpoint("medium") {
            padding: 0 30px 20px 30px;
        }
        .product-addmore-button{
            line-height: 1;
            position: relative;
            margin: 0 0 0 15px;
            color: #000;
            font-weight: 500;
            letter-spacing: 0.02em;
            &:before{
                content: "";
                position: absolute;
                top: 8px;
                width: 9px;
                height: 1px;
                right: calc(100% + 6px);
                display: block;
                background-color: $color-textBase;
            }

            &:after{
                content: "";
                top: 50%;
                width: 1px;
                height: 9px;
                right: calc(100% + 10px);
                display: block;
                position: absolute;
                background-color: $color-textBase;
                transform: translateY(-50%);
            }
        }
    }

    .product-edit-button{
        .button{
            width: 100%;
            margin-bottom: 0;
            font-size: 13px;
            font-weight: 600;
            text-transform: uppercase;
            letter-spacing: 0.05em;
            padding: 18px 30px;

            &:hover, &:active, &:focus {
                color: #fff;
                background-color: #000;
                border-color: #000;
            }

            &:before{
                content: '';
                display: none;
                width: 20px;
                height: 20px;
                border: 2px solid stencilColor('button--primary-color');
                border-radius: 100%;
                border-top: 2px solid transparent;
                -webkit-animation: load-animate infinite linear 1s;
                animation: load-animate infinite linear 1s;
            }

            &.loading{
                pointer-events: none;
                font-size: 0;
                padding-top: 9px;
                padding-bottom: 9px;
                @include breakpoint("medium") {
                    padding-top: 12px;
                    padding-bottom: 11px;
                }
                &:before{
                    display: inline-block;
                    vertical-align: middle;
                }
            }

            &[disabled]{
                pointer-events: none;
            }

            &.button--hidden{
                pointer-events: none;
            }
        }
    }
}
/* End Edit Option */


@media (max-width: 767px) {
    .previewCartItem {
        margin: 0 15px;
        padding: 15px 0;

        .previewCartItem-name {
            padding-left: 15px;
        }
    }

    .preview-cart-total,
    .preview-cart-grand-total {
        margin-left: 15px;
        margin-right: 15px;
    }

    .previewCartAction {
        padding-left: 15px;
        padding-right: 15px;
    }
}

