  // Check if a module is already on the list
  $module_index: index($modules, $name);
  @if (($module_index == null) or ($module_index == false)) {
    $modules: append($modules, $name) !global;
    @content;
  }
}

//
// @functions
//


// RANGES
// We use these functions to define ranges for various things, like media queries.
@function lower-bound($range) {
  @if length($range) <= 0 {
    @return 0;
  }
  @return nth($range, 1);
}

@function upper-bound($range) {
  @if length($range) < 2 {
    @return 999999999999;
  }
  @return nth($range, 2);
}

// STRIP UNIT
// It strips the unit of measure and returns it
@function strip-unit($num) {
  @return $num / ($num * 0 + 1);
}

// TEXT INPUT TYPES

@function text-inputs( $types: all, $selector: input ) {

  $return: ();

  $all-text-input-types:
    text
    password
    date
    datetime
    datetime-local
    month
    week
    email
    number
    search
    tel
    time
    url
    color
    textarea;

  @if $types == all { $types: $all-text-input-types; }

  @each $type in $types {
    @if $type == textarea {
      @if $selector == input {
        $return: append($return, unquote('#{$type}'), comma)
      } @else {
        $return: append($return, unquote('#{$type}#{$selector}'), comma)
      }
    } @else {
      $return: append($return, unquote('#{$selector}[type="#{$type}"]'), comma)
    }
  }

    @return $return;

}

// CONVERT TO REM
@function convert-to-rem($value, $base-value: $rem-base) {
  $value: strip-unit($value) / strip-unit($base-value) * 1rem;
  @if ($value == 0rem) { $value: 0; } // Turn 0rem into 0
  @return $value;
}

@function data($attr) {
  @if $namespace {
    @return '[data-' + $namespace + '-' + $attr + ']';
  }

  @return '[data-' + $attr + ']';
}

// REM CALC

// New Syntax, allows to optionally calculate on a different base value to counter compounding effect of rem's.
// Call with 1, 2, 3 or 4 parameters, 'px' is not required but supported:
//
//   rem-calc(10 20 30px 40);
//
// Space delimited, if you want to delimit using comma's, wrap it in another pair of brackets
//
//   rem-calc((10, 20, 30, 40px));
//
// Optionally call with a different base (eg: 8px) to calculate rem.
//
//   rem-calc(16px 32px 48px, 8px);
//
// If you require to comma separate your list
//
//   rem-calc((16px, 32px, 48), 8px);

@function rem-calc($values, $base-value: $rem-base) {
  $max: length($values);

  @if $max == 1 { @return convert-to-rem(nth($values, 1), $base-value); }

  $remValues: ();
  @for $i from 1 through $max {
    $remValues: append($remValues, convert-to-rem(nth($values, $i), $base-value));
  }
  @return $remValues;
}


@function em-calc($values, $base-value: $rem-base) {
  $remValues: rem-calc($values, $base-value: $rem-base);

  $max: length($remValues);

  @if $max == 1 { @return strip-unit(nth($remValues, 1)) * 1em; }

  $emValues: ();
  @for $i from 1 through $max {
    $emValues: append($emValues, strip-unit(nth($remValues, $i)) * 1em);
  }
  @return $emValues;
}


// Deprecated: OLD EM CALC
@function emCalc($values) {
  @return em-calc($values);
}

layouts/products/productView.scss:
// =============================================================================
// PRODUCT VIEW (CSS)
// =============================================================================
// Product view layout
// -----------------------------------------------------------------------------
.productView {
    .modal & {
        padding-bottom: 0;
        padding-top: 0;
    }
}

.productView-videos-wrap,
.productView-image-wrap {
    opacity: 1;
    visibility: visible;
    height: auto;
    text-align: center;
    &.is-hidden {
    opacity: 0;
    visibility: hidden;
    height: 0;
    }
}

.productView-videos-wrap {
    position: relative;
    z-index: 10;
}

.productView--quickView {
    .socialLinks-item--print {
        display: none;
    }
}

.productView {
    // &:not(.productView--quickView) {
    //     padding-top: 12px;
    // }
    & .productViewTop {
        display: flex;
        @media (max-width: 991px) {
            flex-wrap: wrap;
        }
        @media (max-width: 767px) {
            padding-top: 0;
        }
        & .productView-images {
            width: 380px;
            position: relative;
            padding-bottom: 30px;
            @media (max-width: 1199px) {
                width: 45%;
            }
            @media (max-width: 991px) {
                width: 100%;
            }
            &.current-videos-tab {
                & .sale-badge {
                    display: none;
                }
                & .new-badge {
                    display: none;
                }
            }
        }
        & .productView-details {
            width: calc(100% - 380px);
            padding-bottom: 54px;
            @media (max-width: 1199px) {
                width: 55%;
            }
            @media (min-width: 992px) {
                padding-left: 40px;
            }
            @media (max-width: 991px) {
                width: 100%;
            }
            @media (max-width: 767px) {
                margin-bottom: 10px;
            }
        }
    }
    & .zoom-help {
        display: flex;
        clear: both;
        justify-content: center;
        align-items: center;
        color: #a0a0a0;
        & i.fa {
            margin-left: 12px;
            font-size: 14px;
        }
    }
    &.productView--quickView {
        & .productViewTop {
            border-bottom: 0;
            padding-bottom: 0px;
            margin-bottom: 0px;
        }
    }
}
.productView-nav {
    .productView-image {
        cursor: zoom-in;
        vertical-align: top;
        opacity: 0;
        visibility: hidden;
        &:first-child, &.slick-slide {
            opacity: 1;
            visibility: visible;
        }
        > a {
            display: block;
            position: relative;
            text-align: center;
            width: 100%;
            img {
                margin: 0 auto;
            }
        }
    }
}
.productView-for {
    width: 100%;
    display: block;
    margin-top: 15px;
    padding: 0 30px;
    .slick-track {
        margin: 0 auto;
    }
    .productView-thumbnail {
        border: none;
        outline: none;
        padding: 1px 7px;
        > a {
            position: relative;
            border: 1px solid transparent;
            display: block;
            overflow: hidden;
            &:before {
                content: "";
                display: block;
                padding-top: 130%;
            }
            img {
                top: 50%;
                left: 50%;
                width: auto;
                height: 100%;
                position: absolute;
                transform: translate(-50%,-50%);
            }
        }
    }
    .slick-current.productView-thumbnail {
        > a {
            border: 1px solid #000;
        }
    }
    .slick-arrow-custom {
        width: 21px;
        height: 21px;
        padding: 0;
        margin: 0;
        line-height: 21px;
        font-family: FontAwesome;
        font-size: 0;
        color: #000;
        &:before,
        &:after {
            color: #000;
            font-size: 30px;
            line-height: 21px;
            width: 100%;
            height: 100%;
            background-image: none;
        }
    }
    .slick-prev {
        left: 0;
        &:before {
            content: '\f104';
        }
    }
    .slick-next {
        right: 0;
        &:before {
            content: '\f105';
        }
    }
}

.videoGallery-list {
    .slick-arrow-custom {
        width: 21px;
        height: 21px;
        padding: 0;
        margin: 0;
        line-height: 21px;
        font-family: FontAwesome;
        font-size: 0;
        color: #000;
        &:before,
        &:after {
            color: #000;
            font-size: 30px;
            line-height: 21px;
            width: 100%;
            height: 100%;
            background-image: none;
        }
    }
    .slick-prev {
        left: 0;
        &:before {
            content: '\f104';
        }
    }
    .slick-next {
        right: 0;
        &:before {
            content: '\f105';
        }
    }
}

// Images
// -----------------------------------------------------------------------------

.photos-videos {
    width: 100%;
    display: flex;
    flex-wrap: nowrap;
    align-items: center;
    height: 36px;
    background: #fff;
    & a {
        width: 50%;
        display: flex;
        justify-content: center;
        align-items: center;
        height: 100%;
        font-family: $fontFamily-headings;
        letter-spacing: 0.02em;
        color: #a0a0a0;
        font-size: 11px;
        text-transform: uppercase;
        font-weight: stencilFontWeight('headings-font');
        border-top: 1px solid #e5e5e5;
        background: #f9f9f9;
        -webkit-box-shadow: inset 0px 1px 0px 0px rgba(243, 243, 243, 1);
        -moz-box-shadow: inset 0px 1px 0px 0px rgba(243, 243, 243, 1);
        box-shadow: inset 0px 1px 0px 0px rgba(243, 243, 243, 1);
        &:first-child {
            border-right: 1px solid #e5e5e5;
        }
        &:hover {
            color: #464646;
        }
        &.current-tab {
            color: #464646;
            border-top-color: transparent;
            background: transparent;
            box-shadow: none;
        }
    }
}


.productView-image {
    margin: 0;
    & img {
        width: 100%;
        transition: none;
        -ms-transition: none;
         -webkit-transition: none;
        -moz-transition: none;
        -o-transition: none;
        &:hover {
            cursor: -webkit-zoom-in;
            cursor: -moz-zoom-in;
            cursor: zoom-in;
        }
    }
    & .productView-image--default {
        width: auto;
        margin: 0 auto;
    }
}

.productView-videos {
    align-items: center;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    margin: 0;
}

.productView-thumbnails {
    width: calc(100% - 80px);
    margin-top: 23px;
    margin-left: auto;
    margin-right: auto;
    & .owl-stage {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-wrap: wrap;
        margin-left: auto;
        margin-right: auto;
    }
}

#product-videos.productView-thumbnails {
    & .owl-stage {
        flex-wrap: nowrap;
        margin-left: auto;
        margin-right: auto;
    }
}

.productView-thumbnails {
    & .productView-thumbnail {
        display: flex;
        justify-content: center;
        & a {
            display: inline-block;
            & img {
                -moz-transform-style: unset;
                -webkit-transform-style: unset;
                transform-style: unset;
            }
        }
    }
    & .owl-nav {
        text-align: center;
        margin-top: 0;
    }
    & .owl-nav>div {
        border: 0;
        filter: Alpha(Opacity=100);
        opacity: 1;
        text-decoration: none;
        &:hover {
            color: #000;
            background-color: transparent;
        }
        &.owl-prev {
            left: -40px;
            width: auto;
        }
        &.owl-next {
            right: -40px;
            width: auto;
        }
        &::before {
            font-size: 30px;
        }
    }
    & .owl-item {
        padding: 2px;
    }
    & .productView-thumbnail-link {
        position: relative;
        border: 1px solid transparent;
    }
    & .productView-thumbnail-link:hover {
        opacity: 0.3;
    }
    & .productView-thumbnail-link.is-active {
        border-color: #bfbfbf;
    }
}

.productView--quickView {
    & .productView-details {
        margin-bottom: 0;
    }
}

// Details - Product details
// -----------------------------------------------------------------------------
.productView-product {
    >:last-child {
        margin-bottom: 0;
    }
}

.productView-brand {
    font-size: 13px;
    text-transform: uppercase;
    margin-top: 0px;
    margin-bottom: 13px;
    color: #8d8d8d;
    font-weight: 700;
    letter-spacing: 0.13em;
    & a {
        color: #8d8d8d;
        &:hover {
            color: #000;
        }
    }
}

.productView-title {
    font-size: 20px;
    font-family: $fontFamily-headings;
    font-weight: stencilFontWeight('headings-font');
    margin: 0px 10px 10px 0;
    letter-spacing: 0.02em;
    line-height: 32px;
    @media (max-width: 767px) {
        margin-top: 0;
        padding-top: 5px;
    }
}

.productView-rating {
    padding-top: 8px;
    padding-bottom: 27px;
    & i.fa {
        font-size: 13px;
        vertical-align: middle;
    }
}

.productView-reviewLink {
    display: inline-block;
    margin-left: 4.5rem;
    vertical-align: middle;
    @media (max-width: 480px) {
        margin-left: 20px;
        margin-right: 20px;
    }
    &>a {
        color: #000;
        &:hover {
            color: $anchor-font-color-hover;
            border-bottom: 1px solid $anchor-font-color-hover;
        }
    }
    &+.productView-reviewLink {
        font-weight: 700;
        margin-left: 55px;
        @media (max-width: 480px) {
            margin-left: 0;
            margin-right: 0;
        }
        &>a {
            border-bottom: 1px dotted #000;
            &:hover {
                color: $anchor-font-color-hover;
                border-bottom-color: $anchor-font-color-hover;
            }
        }
        @media (max-width: 360px) {
            margin-left: 0;
            margin-top: 10px;
            display: block;
        }
    }
}

.productView:not(.productView--quickView) {
    &.reviewLinkCount {
        cursor: pointer;
    }
}

// Details - Product price
// -----------------------------------------------------------------------------
.productView-price {
    font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
    font-size: 20px;
    padding-bottom: 30px;
    font-weight: stencilFontWeight('headings-font');
    color: stencilColor('basic-price-color');
    .price-section--saving {
        font-family: $fontFamily-sans;
        font-size: fontSize("base");
        color: stencilColor('saving-price-color');
        font-weight: normal;
        font-style: italic;
    }
    .price.price--rrp {
        color: stencilColor('retail-price-color');
        font-size: 18px;
        margin-right: 13px;
        font-weight: 400;
        &~.price--withoutTax {
            color: stencilColor('sale-price-color');
        }
    }
    abbr {
        border: 0;
        color: inherit;
        font-size: inherit;
    }
    .price-section--withoutTax~.price-section--saving {
        margin-top: 12px;
    }
    p[translate] {
        color: $anchor-font-color-hover;
    }
}

// Details - Product Information
// -----------------------------------------------------------------------------
.productView-info {
    overflow: hidden;
    margin-bottom: 0;
    padding-bottom: 19px;
}

.productView-info-name,
.productView-info-value {
    float: left;
}

.productView-info-name {
    clear: both;
    margin-bottom: spacing("base")/2;
    margin-right: 30px;
    width: 110px;
    font-family: $fontFamily-sans;
    font-weight: stencilFontWeight('headings-font');
    font-size: fontSize("base");
    letter-spacing: 0.02em;
}

.productView-info-value {
    margin-bottom: spacing("base");
    padding-top: 1px;
}

[data-custom-field-name="has-size-chart"] {
    display: none;
}

// Details - Product options
// -----------------------------------------------------------------------------
//
// 1. Align the product options list item radios vertically.
// 2. Resetting font due to inline-block whitespace issue.
//
// -----------------------------------------------------------------------------
.productView-options {
    clear: both;
    @include clearfix;
    margin-bottom: 10px;
    @include breakpoint("small") {
        text-align: left;
    }
    .form-input[type="number"] {
        @include breakpoint("small") {
            width: 100px;
        }
    }
    [data-product-attribute] .form-option{
        position: relative;
    }
    .form {
        display: inline;
        @media (max-width: 480px) {
            display: block;
            margin-bottom: 10px;
        }

        #add-to-cart-wrapper {
            display: inline;
        }
    }
    .form-field {
        line-height: 1;
        font-size: 0; // 2
        margin-bottom: 15px; // scss-lint:disable SelectorDepth, NestingDepth
        position: relative;
        
        >.form-checkbox+.form-label,
        >.form-radio+.form-label {
            display: inline-block;
            margin-left: spacing("single");
            width: auto;
            @include breakpoint("small") {
                margin-left: 0;
                width: 100%;
            }
        }
        >.form-label:nth-of-type(2) {
            margin-left: 0;
        }
        input[type="file"] {
            font-size: fontSize("base");
        }
    }
    .qty-add-wrap {
        display: inline;
        & .form-field--increments {
            width: 155px;
        }
    }
    .form-field--increments {
        margin-bottom: 30px;
        & button {
            width: 41px;
            height: 42px;
            &[data-action="dec"] {
                border-top-right-radius: 0;
                border-bottom-right-radius: 0;
                margin-right: -1px;
                & i.fa {
                    font-size: 20px;
                }
            }
            &[data-action="inc"] {
                border-top-left-radius: 0;
                border-bottom-left-radius: 0;
                margin-left: -1px;

                & i.fa {
                    font-size: 20px;
                }
            }
        }
        & .form-input {
            border-radius: 0;
            font-weight: stencilFontWeight('headings-font');
        }
    }
    .form-action {
        @include breakpoint("small") {
            float: left;
            padding: 0 remCalc(10);
            width: 50%;
        }
        @include breakpoint("medium") {
            float: none;
            padding: 0;
            width: auto;
        }
        @include breakpoint("large") {
            display: inline-block;
            margin-top: 1rem;
        } // scss-lint:disable SelectorDepth, NestingDepth
        .button {
            width: 100%;
            @include breakpoint("large") {
                margin-right: spacing("half");
                width: auto;
            }
        }
    }
}

// Product list (with images)
// -----------------------------------------------------------------------------
.productOptions-list {
    border: $productOptions-list-border;
    list-style: none;
    margin: 0;
}

.productOptions-list-item {
    padding: $productOptions-list-item-padding;
    +.productOptions-list-item {
        border-top: $productOptions-list-item-borderTop;
    }
    .form-label {
        text-align: left;
        top: -2px; // 1
    }
}

.productOptions-list-item-figure,
.productOptions-list-item-content {
    display: table-cell;
    vertical-align: middle;
}

.productOptions-list-item-figure {
    width: $productOptions-list-item-figure-width;
}

.productOptions-list-item-content {
    padding-left: $productOptions-list-item-content-paddingLeft;
}

// Swatch Size Changes
// -----------------------------------------------------------------------------
// do some math
$value_of_swatch_size: stencilString("swatch_option_size");
$position_of_x: str-index($value_of_swatch_size, "x");
$first_value: str-slice($value_of_swatch_size, 0, $position_of_x - 1);
$second_value: str-slice($value_of_swatch_size, $position_of_x + 1);

[data-product-attribute] .form-option {
    overflow: inherit;
}

[data-product-attribute] .form-option.form-option-swatch {
    overflow: visible;
}

.form-option-variant--color,
.form-option-variant--pattern {
    height: 36px;
    width: 36px;
}

.form-option {
    display: inline-block;
}

.form-option-expanded {
    background-color: stencilColor("body-bg");
    border: 1px solid stencilColor("input-border-color-active");
    left: 1px;
    opacity: 0;
    padding: 10px;
    position: absolute !important;
    top: calc(100% + 5px);
    transition: opacity 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
    visibility: hidden;
    float: left;
    border-radius: 10px;
}

.productView-details .form-option:hover {
    .form-option-expanded {
        opacity: 1; //transform: translate(-50%, 0);
        visibility: visible;
        z-index: 5000;
        &>span {
            text-align: center;
            &>span {
                font-size: 12px;
                float: left;
                width: 100%;
            }
            &>img {
                max-height: 90%;
                margin: 5px auto 0 auto;
                display: inline-block;
            }
        }
    }
}

.form-option-image {
    background: {
        position: 50%;
        repeat: no-repeat;
        size: cover;
    }
    display: block;
    height: 200px;
    width: 200px;
}
.btn.btn-outofstock{
    color: stencilColor('button--outOfStock-color')!important;
    background-color: stencilColor('button--outOfStock-backgroundColor')!important;
    border-color: stencilColor('button--outOfStock-borderColor')!important;
    &:hover {
        color: stencilColor('button--outOfStock-colorHover')!important;
        background-color: stencilColor('button--outOfStock-backgroundColorHover')!important;
        border-color: stencilColor('button--outOfStock-borderColorHover')!important;
    }
}
// AddToButtons
.out-stockWarp.btn {
    min-width: 100px;
    height: 48px;
    margin-right: 10px;
    margin-bottom: 15px;
    width: -webkit-calc(50% - 10px);
    width: -moz-calc(50% - 10px);
    width: -ms-calc(50% - 10px);
    width: calc(50% - 10px);
    line-height: 35px;
    max-width: 280px;
    height: 52px;
    display: inline-flex;
    justify-content: center;
    text-align: center;
    color: stencilColor('button--outOfStock-color');
    background-color: stencilColor('button--outOfStock-backgroundColor');
    border-color: stencilColor('button--outOfStock-borderColor');
    cursor: not-allowed;
    @media (max-width: 991px) {
        float: left;
        margin-right: 10px;
        display: inline-block;
        max-width: 100%;
    }
    @media (min-width: 992px) and (max-width: 1200px) {
        max-width: 100%;
        text-align: center;
    }
    @media (max-width: 480px) {
        display: block;
        width: 100%;
        margin-right: 0;
    }
    & span{
        display: block;
        text-align: center;
    }
    &:hover {
        color: stencilColor('button--outOfStock-colorHover');
        background-color: stencilColor('button--outOfStock-backgroundColorHover');
        border-color: stencilColor('button--outOfStock-borderColorHover');
    }
}

[data-cart-item-add] button#form-action-addToCart {
    min-width: 270px;
    height: 48px;
    margin-right: 10px;
    margin-bottom: 15px;
    width: 100%;
    max-width: 280px;
    height: 52px;
    display: inline-flex;
    justify-content: center;
    text-align: center;
    color: stencilColor('detail-add-to-cart-color');
    background-color: stencilColor('detail-add-to-cart-bgColor');
    border-color: stencilColor('detail-add-to-cart-borderColor');
    @media (max-width: 991px) {
        float: left;
        margin-right: 10px;
        display: inline-block;
    }
    @media (min-width: 992px) and (max-width: 1200px) {
        max-width: 100%;
