//Product Tabs
.responsive-tabs {
    @media (max-width: 991px) {
        padding-bottom: 30px;
    }
    .state {
        position: absolute;
        left: -10000px;
    }
    .flex-tabs {
        display: -webkit-box;
        display: -ms-flexbox;
        display: flex;
        -webkit-box-align: center;
        -ms-flex-align: center;
        align-items: center;
        justify-content: flex-start;
        flex-wrap: wrap;
        border: 0;
        label.tab {
            position: relative;
            .nameTab{
                margin-bottom: 0;
                width: 100%;
                height: 100%;
                display: -webkit-box;
                display: -ms-flexbox;
                display: flex;
                -webkit-box-align: center;
                -ms-flex-align: center;
                align-items: center;
                padding: 0 30px;
            }
            &.current{
                .toggle-expand::before {
                    position: absolute;
                    top: 30%;
                    right: 17px;
                    border: none;
                    font-family: FontAwesome;
                    content: "\f077";
                }
            }
        }
        .toggle-expand::before {
            position: absolute;
            font-family: FontAwesome;
            content: "\f078";
            top: 30%;
            right: 17px;
            border: none;
        }
        
        span.toggle-expand{
            position: absolute;
            top: 0;
            right: 0;
            width: 50px;
            height: 50px;
            cursor: pointer;
            font-size: 10px;
            font-weight: 400;
            @media(min-width: 992px){
                display: none;
            }
        }

        .panel {
            background-color: #fff;
            padding-top: 35px;
            padding-bottom: 1.5rem;
            display: none;
            width: 100%;
            flex-basis: auto;
            border-top: 1px solid #e5e5e5;
            margin-top: -0.5px;
            @media (max-width: 991px) {
                min-height: auto;
            }
        }
    }
    .tab {
        position: relative;
        bottom: -1px;
        display: flex;
        align-items: center;
        float: left;
        height: 52px;
        margin-left: -1px;
        border: 0;
        font-family: $fontFamily-headings;
        font-size: 13px;
        letter-spacing: 0.12em;
        font-weight: 700;
        transition: background .3s ease 0s;
        text-transform: uppercase;
        color: #000;
        border: 1px solid #e5e5e5;
        background: #f7f8f9;
        outline: none;
        z-index: 1;
        &:hover {
            background: #f7f8f9;
            cursor: pointer;
        }
    }
}

