        width: -ms-calc(50% - 10px);
        width: calc(50% - 10px);
        height: 52px;
        line-height: 48px;
        float: right;
        position: relative;
        display: inline-block;
        text-align: center;
        margin-left: 10px;
        color: #000;
        border: 2px solid #000;
        background-color: #fff;

        &:hover, &:active, &:focus {
            color: #ffffff;
            borer-color: #000;
            background-color: #000;
        }

        i {
            font-size: 18px;
            vertical-align: middle;
            position: relative;
            top: -1px;
            font-weight: 700;
            display: none;
        }

        span {
            font-size: 13px;
            font-weight: 600;
            letter-spacing: 0.1em;
            text-transform: uppercase;
        }
    }

    ul {
        margin-top: 15px;
        padding: 0;
        border: none;

