/* Start Bundle Product */
.halo-bundle-products{
    padding-top: 40px;
    padding-bottom: 40px;
    margin: 0 0 40px 0;
    border-top: 1px solid #eeeeee;
    .halo-block-header{
        text-align: center;
    }

    .bundle-products-wrapper{
        display: block;
    }

    .bundle-product-left{
        width: 100%;
    }

    .bundle-product-right{
        width: 100%;
        margin: 50px 0 0 0;
    }

    .halo-product-list{
        padding: 0;
        margin: 0 -15px;
        width: auto;

        &:not(.slick-initialized){
            display: block;
            white-space: nowrap;
            overflow: hidden;
            font-size: 0;
            letter-spacing: 0;
            position: relative;

            .halo-product-item{
                display: inline-block;
                vertical-align: top;
                width: auto;
                opacity: 0;
                visibility: hidden;
            }

            &:before{
                content: '';
                background: url(../img/loading.svg) no-repeat;
                background-size: contain;
                display: block;
                position: absolute;
                width: 200px;
                height: 200px;
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%);
            }
        }
        .slick-dots li button {
            width: 11px;
            height: 11px;

            &::before {
                width: 11px;
                height: 11px;
            }
        }
    }

    .halo-product-item{
        padding: 0 15px;

        .halo-product-image{
            position: relative;
            padding: 0 20px;
            &:after{
                content: '';
                position: absolute;
                top: 0;
                bottom: 0;
                left: 0;
                right: 0;
                background: #ffffff;
                opacity: .6;
                z-index: 1;
            }
        }


        &.isChecked{
            .halo-product-image{
                &:after{
                    display: none;
                }
            }
        }
    }

    .halo-product-link{
        display: block;
        position: relative;
    }

    .halo-product-container{
        display: block;
        position: relative;

        img{
            position: absolute;
            top: 0;
            bottom: 0;
            left: 0;
            right: 0;
            margin: auto;
            opacity: 1;
            visibility: visible;
            border: 0;
            width: 100%;
            max-height: 100%;
            object-fit: contain;
            font-family: 'object-fit: contain;';
            transition: $anchor-transition;
        }

        &:before{
            content: '';
            display: block;
            height: 0;
            width: 100%;
            padding-bottom: 138.88889%;
        }
    }

    .halo-product-content{
        padding: 20px 0 0 0;
    }

    .halo-detail-label{
        display: inline-block!important;
        vertical-align: top;
        padding: 0;
        margin: 0;
        &:before{
            position: absolute;
            display: inline-block;
            vertical-align: top;
            top: 1px;
            background: transparent;
        }

        &:after{
            top: 2px;
        }
    }
    
    .halo-detail-checkbox{
        &[disable]{
            + .halo-detail-label{
                &:before{
                    border-color: #ccc;
                    background: #fff;
                }
            }
        }

        &:checked{
            + .halo-detail-label{
                &:before{
                    border-color: #ccc;
                    background: #fff;
                }
            }
        }
    }

    .halo-detail-name{
        min-height: 38px;
        color: $card-title-color;
        font-weight: 500;
        line-height: 1.625;
        padding: 0 0 0 25px;
        letter-spacing: 0;
        display: -webkit-box;
        -webkit-line-clamp: 2;
        overflow: hidden;
        text-overflow: ellipsis;
        -webkit-box-orient: vertical;
        /* autoprefixer: off */
        span{
            color: #787878;
            font-weight: 400;
            border-bottom: 1px solid #bbbbbb;
        }
    }

    .halo-detai-price{
        font-size: $fontSize-root;
        line-height: 1;
        position: relative;
        margin: 9px 0 0 0;
        display: block;
        color: $color-textSecondary;

        .price-section{
            display: inline-block;
            vertical-align: top;
            font-weight: 600;
            font-size: 13px;
            &.price-section--sale{
                color: #e95144;
            }
            &.price-section--saving {
                display: none !important;
            }
            .price--withTax,
            .price--withoutTax {
                color: #000;
            }
            .price--rrp {
                margin-right: 5px;
                + .price--withTax {
                    color: #e95144;
                }
                + .price--withoutTax {
                    color: #e95144;
                }
            }
        }
    }

    .halo-toggle-options{
        margin: 25px 0 0 0;
        width: 100%;
        color: #000;
        border: 1px solid #000;
        background-color: #ffffff;

        &.is-focus, &:hover, &:active, &:focus{
            color: #ffffff;
            border-color: #000;
            background-color: #000;
        }
    }

    .halo-detail-options{
        display: none;
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%,-50%);
        background: #ffffff;
        z-index: 101;
        padding: 20px;
        width: 300px;
        box-shadow: 0 0 10px rgba(0,0,0,0.1);
        transition: $anchor-transition;

        .halo-option-close{
            position: absolute;
            top: 0;
            right: 0;
            width: 50px;
            height: 50px;
            z-index: 10;
            font-size: 30px;
            font-weight: 300;
            line-height: 46px;
            text-align: center;
            pointer-events: auto;
            color: #191919;
            background-color: transparent;
        }

        .alertBox-message{
            font-style: italic;
            line-height: 1.75;
            color: #f08584;
        }

        .form-field {
            font-size: 0;
            margin: 0 0 15px 0;
            padding: 0;
            line-height: 1;

            > .form-label{
                small{
                    color: #dd674e;
                    float: none;
                }

                [data-option-value]{
                    color: $color-textSecondary;
                    text-transform: capitalize;
                }
            }

            + .form-field{
                margin-top: 0;
            }

            &:last-child{
                margin-bottom: 0;
            }
        }

        &.is-open{
            display: block;
        }
    }

    .halo-product-total{
        width: 100%;
        max-width: 270px;
        margin: 0;
        display: block;
        text-align: left;

        .total-price{
            display: block;
            line-height: 1.625;
            margin: 0 0 18px 0;
        }

        .text{
            color: #000;
            font-weight: 500;
        }

        .price{
            font-size: 16px;
            font-weight: 600;
            margin: 0 0 0 5px;
        }

        .button{
            width: 100%;
            font-size: 13px;
            font-weight: 700;
            letter-spacing: 1px;
            text-transform: uppercase;
            padding: 19px 35px;
            &:hover, &:active, &:focus {
                color: #000;
                border-color: #000;
                background-color: #fff;
            }
        }
    }

    .slick-arrow{
        width: 34px;
        height: 35px;
        top: -23px;
        transform: translateY(-50%);
        border: 2px solid #000;
        font-family: "FontAwesome";
        display: flex !important;
        -ms-flex-pack: center;
        justify-content: center;
        -ms-flex-align: center;
        align-items: center;
        color: #000;
        padding: 0;
        z-index: 1;
        &:before {
            width: auto;
            height: auto;
            background: none;
            font-size: 14px;
            position: relative;
            display: block;
            color: #000;
        }

        &.slick-disabled {
            opacity: 1;

            &:before {
                opacity: 1;
                color: #cecece;
            }
        }

        &.slick-prev{
            left: auto;
            right: -191px;
            border-right: none;

            &:before {
                content: "\f053";
            }
        }
        &.slick-next{
            left: auto;
            right: -225px;

            &:before {
                content: "\f054";
            }
        }

        @media(max-width: 1024px) {
            display: none;
        }
    }

    .slick-dots{
        bottom: -45px;
    }

    .slick-slide{
        + .slick-active{
            .halo-detail-options{
                left: 15px;
                transform: translateY(-50%);
            }
        }
    }

    .slick-active{
        position: relative;

        + .slick-active{
            .halo-product-image{
                &:before{
                    content: "+";
                    color: #bfbfbf;
                    font-size: 35px;
                    font-weight: 300;
                    position: absolute;
                    left: -28px;
                    top: 50%;
                    transform: translateY(-50%);
                }
            }
        }

        &:first-of-type{
            .halo-detail-options{
                left: 15px;
                transform: translateY(-50%);
            }
        }

        &:last-of-type{
            .halo-detail-options{
                left: auto;
                right: 15px;
                transform: translateY(-50%);
            }
        }

        + .slick-slide{
            .halo-detail-options{
                left: auto;
                right: 15px;
                transform: translateY(-50%);
            }
        }
    }

    .halo-block-header{
        text-align: left;
        & .title {
            font-size: 16px;
            font-weight: 700;
            margin: 0 0 45px;
            text-transform: uppercase;
        }
    }

    > .container {
        padding: 0;
    }

    @media (min-width: 375px){
        .halo-bundle-products{
            .halo-detail-options{
                width: 320px;
            }
        }
    }

    @media (min-width: 768px){
        padding-top: 60px;
        padding-bottom: 60px;
        margin-bottom: 60px;
    }

    @media (min-width: 1025px){
        padding-top: 40px;
        padding-bottom: 40px;
        margin-bottom: 40px;

        .bundle-products-wrapper{
            display: flex;
            justify-content: space-between;
            align-items: center;
        }

        .bundle-product-left{
            width: calc(100% - 240px);
        }

        .bundle-product-right{
            width: 210px;
            margin-top: 0;
            margin-left: 30px;
        }

        .halo-product-total{
            max-width: 100%;
            text-align: center;
        }
    }

    @media (min-width: 1400px){
        .container{
            padding-left: calc((100% - 1170px)/2);
            padding-right: calc((100% - 1170px)/2);
        }
    }

    @media (min-width: 1600px){
        .container{
            padding-left: calc((100% - 1280px)/2);
            padding-right: calc((100% - 1280px)/2);
        }
    }

    @media (min-width: 1920px){
        .container{
            padding-left: calc((100% - 1450px)/2);
            padding-right: calc((100% - 1450px)/2);
        }
    }
}
/* End Bundle Product */

.productView--quickView [data-wishlist-add] {
    position: relative;
}

#wishlist-dropdown-q.is-open {
    position: absolute;
    top: calc(100% + 15px);
    left: 10px;
    min-width: 200px;
}

.productView-nextProducts {
    display: none;

    @media(min-width: 1025px) {
        display: block;
        position: absolute;
        top: -78px;
        right: 0;
    }

    @media(min-width: 1025px) and (max-width: 1199px) {
        right: 15px;
    }

    .next-prev-icons {
        > a.disable {
            color: #cecece;
        }
        > a {
            font-size: 0;
            display: inline-block;
        }

        .icon {
            border: 0;
            border-radius: 0;
            width: 25px;
            height: 31px;
            font-family: "FontAwesome";
            padding: 6px 0;

            &:before {
                font-size: 14px;
            }

            &.prev {
                text-align: left;
                &:before {
                    content: "\f053";
                }
            }

            &.next {
                text-align: right;
                &:before {
                    content: "\f054";
                }
            }
        }
    }
    .next-prev-modal {
        display: none;
        width: 350px;
        position: absolute;
        top: 100%;
        right: 0;
        z-index: 3;
        padding-top: 10px;

        &.is-active {
            display: block;
        }
        
    }
    .next-prev-content {
        display: none;
        width: 100%;
        padding: 0;
        background-color: #fff;
        border-radius: 0;
        box-shadow: 0 0 2px 1px rgba(0,0,0,.1);
        &.is-show {
            display: block;
        }
    }
    .card {
        margin-bottom: 0;
        .card-image {
            display: table-cell;
            vertical-align: top;
            position: relative;
            overflow: hidden;
            z-index: 1;
            width: 70px;
            border: 0;
            .card-link {
                width: 100%;
            }
        }
        .card-content {
            display: table-cell;
            vertical-align: middle;
            width: calc(100% - 70px);
            padding: 10px 20px;

            .card-title {
                margin: 0 0 8px;
                margin-top: -3px;
                text-align: left;
                font-size: 1rem;
                font-weight: 500;
                line-height: 20px;
                letter-spacing: 0;
            }
            .card-price {
                line-height: 1;
                position: relative;
                margin: 0 0 16px;
                visibility: visible;
                margin-bottom: 0;
                color: black;
                font-size: 13px;
                font-weight: 700;
                line-height: 25px;

                .price--rrp {
                    color: #8d8d8d;
                    margin-right: 5px;
                    font-weight: 500;

                    ~ .price--withTax,
                    ~ .price--withoutTax {
                        color: #e95144;
                    }
                }

                .price-section--saving {
                    display: none;
                }
            }
        }
    }
}

/* Sticky Add To Cart */
#sticky_addtocart{
    position: fixed;
    left: 0;
    right: 0;
    top: 75px;
    width: 100%;
    z-index: -1;
    padding: 0;
    text-align: center;
    background: #f7f8f9;
    transition: all 0.5s ease;
    box-shadow: 0px 0px 4px rgba(0,0,0,0.2);
    opacity: 0;
    visibility: hidden;

    > .container {
        position: unset;
        width: 100%;
        max-width: 100%;
        padding: 0 0 0 40px;

        @media(max-width: 1199px) {
            padding: 0 0 0 15px;
        }

        @media(max-width: 479px) {
            padding: 0;
        }

        &:before, &:after {
            content: none;
        }
        .item-wrap {
            display: flex;
            position: static;
            align-items: center;
            justify-content: center;
            justify-content: space-between;
        }
        .item {
            width: 100%;
            padding: 0 15px;
            display: inline-block;
            @include breakpoint("small") {
                width: 66.66%;
            }
            @include breakpoint("medium") {
                width: 50%;
            }
            
            &:first-child {
                display: none;
                padding-left: 0;
                @include breakpoint("small") {
                    display: inline-block;
                }
                @media (min-width: 551px) and (max-width: 767px) {
                    width: calc(100% - 175px);
                }
            }
            &:last-child {
                padding-right: 0;
                @media (min-width: 551px) and (max-width: 767px) {
                    width: 175px;
                }
                @media (max-width: 550px) {
                    padding-left: 0;
                }
            }
            &.has-countDown {
                width: 100%;
                @media (min-width: 551px) {
                    width: 60%;
                }
                @media (min-width: 768px) {
                    &.product-info {
                        width: 35%;
                    }
                    &.item-right {
                        width: 65%;
                    }
                }
                @media (min-width: 1280px) {
                    &.product-info {
                        width: 40%;
                    }
                    &.item-right {
                        width: 40%;
                    }
                }
            }
            &.product-info {
                .productView-title {
                    margin-bottom: 0;
                    font-size: 12px;
                    font-weight: 500;
                    color: stencilColor('color-textHeading');
                    display: -webkit-box;
                   -webkit-box-orient: vertical;
                   -moz-box-orient: vertical;
                    overflow: hidden;
                    position: relative;
                    word-wrap: break-word;
                    -webkit-line-clamp: 1;
                    text-overflow: ellipsis;
                    letter-spacing: 0.02em;
                    padding: 0;

                    .color-name {
                        color: stencilColor('card-saving-price-color');
                    }
                }
            }
            &.count-down {
                display: none;
                @media (min-width: 768px) {
                    width: 20%;
                }
                &.has-countDown {
                    display: none;
                    @media (min-width: 1200px) {
                        display: inline-block;
                    }
                }
            }
            .productCountDown {
                margin: 0;
                text-align: center;
                .CountDownText {
                    display: inline-block;
                    padding-top: 2px;
                    margin-right: 5px;
                    background-position-y: -1px;
                    margin-bottom: 0;
                    border-bottom: 0;

                    .CountDown-icon {
                        width: 18px;
                        height: 18px;
                    }
                }
                .product-count-down {
                    display: inline-block;
                }
            }
            .countDowntimer2 {
                margin-bottom: 0;

                .clock-item {
                    font-size: 13px;
                    background: none;
                    border: none;
                    padding: 0;
                    color: #2d2d2d;
                    margin-bottom: 0;
                    display: inline-block;
                }
            }
            
            &.item-right {
                text-align: right;
                @media (min-width: 551px) and (max-width: 767px) {
                    width: 40%;
                }
                .productView-price {
                    margin-top: 0;
                    margin-bottom: 0;
                    margin-right: 15px;
                    height: 40px;
                    line-height: 40px;
                    display: none;
                    @media (min-width: 768px) {
                        display: inline-block;
                    }
                    @include breakpoint("large") {
                        margin-right: 30px;
                    }
                    .price-section {
                        margin-bottom: 0;
                        font-size: 13px;
                        font-weight: 600;

                        .price.price--rrp {
                            font-size: 13px;
                            font-weight: 600; 
                            margin-right: 3px;
                        }

                        &.price-section--saving {
                            display: none !important;
                        }
                    }
                }
            }
        }
    }
    .productView-countDown {
        display: none;
        @include breakpoint("medium") {
            display: inline-block;
            margin-right: 5px;
            > .countDownDate {
                box-shadow: none;
                border: none;
            }
            .block-time {
                width: 55px;
                height: 55px;
                font-size: 14px;
                line-height: 20px;
                font-weight: 600;
                text-align: center;
                display: inline-block;
                vertical-align: middle;
                border-radius: 50%;
                padding: 10px 0 11px 0;
                margin: 0 5px 5px 5px;
                position: relative;
                &:after {
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 0;
                    content: "";
                    display: block;
                    opacity: 0.6;
                    position: absolute;
                    border-radius: 50%;
                    border: 1px solid #ebebeb;
                }
                &:first-child {
                    margin-left: 0;
                }
                &:last-child {
                    margin-right: 0;
                }
            }
            .num {
                font-size: 16px;
                line-height: 1;
            }
            .block-label {
                font-size: 12px;
                margin: 0;
                text-transform: capitalize;
            }
        }
    }
    .product-wrapper {
        display: table;
        text-align: left;

        > .product-image {
            display: table-cell;
            vertical-align: middle;
            width: 60px;

            span {
                color: #787878;
                font-weight: 400;
                border-bottom: 1px solid #bbbbbb;
            }
        }

        > .product-content {
            display: table-cell;
            vertical-align: middle;
        }
        
        .productView-price {
            font-size: 12px;
            margin-bottom: 0;
            .price-section {
                font-size: 12px;
            }
        }
    }
    .form-field--increments {
        margin: 0px 15px 0 0;
        font-size: 0;
        display: none;
        @include breakpoint("medium") {
            display: inline-block;
        }
        .form-increment {

            .form-input--incrementTotal {
                font-weight: 700;
                min-width: 52px;
            }

            .button {
                background-color: #fff;
            }
        }
        .form-label {
            display: none;
        }
    }
    .form-action {
        position: relative;
        width: 100%;
        height: 100%;
        display: inline-block;
        @include breakpoint("small") {
            max-width: 170px
        }
        @media (min-width: 1440px) {
            max-width: 215px;
        }
        input {
            margin-bottom: 0;
            width: 100%;
            height: 52px;
        }
        .button {
            width: 100%;
            margin: 0;
            position: absolute;
            left: 0;
            top: 0;
            right: 0;
            bottom: 0;
            opacity: 1;
            visibility: visible;
            z-index: 5;
            padding: 12px 15px;
            font-weight: 600;
            height: 52px;
            line-height: 26px;
            font-size: 13px;
            text-transform: uppercase;
            letter-spacing: 0.1em;
            border: 2px solid transparent;

            &:hover, &:active, &:focus {
                border-color: #000;
            }

            &.is-active {
                opacity: 0;
                visibility: hidden;
            } 
        }
    }
    .show_select_options{
        color: black;
        border: 1px solid #cbcbcb;
        background-color: rgba(0, 0, 255, 0);
        min-width: 209px;
        font-size: 12px;
        line-height: 24px;
        font-size: 12px;
        line-height: 24px;
        border-radius: 3px;
        letter-spacing: 0.65px;
        text-transform: uppercase;
        transition: all 0.3s ease;
        font-family: "Poppins", Arial, Helvetica, sans-serif;
    }

    .form {
        margin: 0;
        display: -ms-flexbox;
        display: -webkit-flex;
        display: flex;
        -ms-flex-align: center;
        -webkit-align-items: center;
        align-items: center;
        -ms-flex-pack: flex-end;
        -webkit-justify-content: flex-end;
        justify-content: flex-end;
        height: 52px;

        .pop-up-option {
            left: 0;
            right: 0;
            top: 100%;
            width: 100%;
            z-index: 1;
            position: absolute;
            text-align: left;
            background: #ffffff;
            opacity: 0;
            overflow: visible;
            visibility: hidden;
            transition: all .3s ease;
            padding: 25px 0 0;
            box-shadow: 0px 2px 2px rgba(0,0,0,0.2);

            @media(max-width: 479px) {
                padding: 15px 0 0;
            }

            &.is-open {
                visibility: visible;
                opacity: 1;
            }

            > .container {
                width: 100%;
                max-width: 100%;
                padding: 0 170px 0 40px;

                @media(min-width: 1440px) {
                    padding-right: 215px;
                }

                @media(max-width: 1199px) {
                    padding: 0 170px 0 15px;
                }

                @media(max-width: 767px) {
                    padding: 0 70px 0 15px;
                }

                @media(max-width: 479px) {
                    padding: 0 15px;
                }
            }

            .data-product-name {
                display: block;
                padding-top: 0;
                margin-bottom: 15px;
                padding-bottom: 10px;
                border-bottom: 1px solid #f8f8f8;
                padding-right: 20px;

                @include breakpoint("small") {
                    display: none;
                }

                .productView-title {
                    font-size: 12px;
                    font-weight: 500;
                    line-height: 20px;
                    padding-top: 0;
                    margin-bottom: 0px;
                }

                .productView-price {
                    display: inline-block;
                    height: auto;
                    line-height: unset;
                    padding-bottom: 0;

                    .price-section {
                        font-size: 13px;
                    }
                }
            }
            .data-product-option {
                font-size: 0;
                display: block;
                width: 100%;
                max-height: 300px;
                @include breakpoint("small") {
                    position: relative;
                }
            }
            .form-field {
                width: 100%;
                font-size: 12px;
                margin: 0 0 11px 0;
                display: block;
                vertical-align: top;
                position: relative;
                font-size: 0;

                @media(min-width: 1200px) {
                    display: inline-block;
                    vertical-align: top;
                    width: 50%;
                    &:nth-child(even) {
                        padding-left: 15px;
                    }
                    &:nth-child(odd) {
                        padding-right: 15px;
                    }
                }

                .form-label {
                    display: inline-block;
                    vertical-align: middle;
                    margin-right: 15px;
                    margin-bottom: 14px;
                }

                @media(max-width: 479px) {
                    .form-label--alternate {
                        display: block;
                        margin-right: 0;
                        margin-bottom: 10px;
                    }

                    .form-option {
                        margin-bottom: 10px;
                    }
                }

                .form-option {
                    margin-bottom: 10px;
                    position: relative;
                }

                > .form-checkbox + .form-label
                {
                    display: inline;
                    width: auto;        
                }

                &[data-product-attribute] .form-option {
                    display: inline-block;
                    vertical-align: middle;
                }
                .form-select {
                    max-width: 530px;
                }
            }
            .close {
                font-size: 26px;
                position: absolute;
                width: 22px;
                height: 22px;
                line-height: 20px;
                opacity: 1;
                background: #fff;
                filter: alpha(opacity=100);
                text-align: center;
                font-weight: 600;
                z-index: 10;
                top: 5px;
                right: 40px;
                color: #e95144;

                @media(max-width: 767px) {
                    top: 0;
                    right: 25px;
                }

                @media(max-width: 479px) {
                    right: 15px;
                }
            } 
        }
        .alertBox--error {
            min-width: 200px;
            margin: 0;
            opacity: 0.65;
            width: 100%;
            color: #fff;
            border-radius: 0;
            text-align: center;
            padding: 10px 25px;
            pointer-events: none;
            background-color: #505050;
            border: 1px solid #505050;
            .alertBox-icon {
                display: none;
            }
            .alertBox-message {
                font-size: 13px;
                line-height: 20px;
                text-transform: uppercase;
                letter-spacing: 0.05rem;
                font-weight: 700;
                padding: 0;
                text-align: center;
                display: block;
            }
        }
    }
}

.show_sticky#sticky_addtocart {
    z-index: 999;
    opacity: 1;
    visibility: visible;
}
body.is-stickyAddToCart.is-sticky #menu.navPages-container {
    -webkit-box-shadow: none;
    box-shadow: none;
}
/* End Sticky Add To Cart */

layouts/container/container.scss:
.container {
    margin-left: auto;
    margin-right: auto;
    position: relative;
    min-width: 300px;
    @media (min-width: 1200px) {
        padding: 0;
    }
    @media (max-width: 1199px) {
        width: 100%;
    }
    @media (max-width: 991px) {
        width: 100%;
    }
    .page.row {
        margin: 0;
    }
}

.container_page {
    margin: 0 auto;
    padding-bottom: 80px;
    border-top: 1px solid  #eaeaea;
}
.page-type-default .container_page {
    padding-bottom: 0;
    border-top: 0;
}

.page-type-product .container_page {
    padding-bottom: 70px;
}
.page-type-search .page > .row, .module-wrapper.productGrid {
    margin-left: 0;
    margin-right: 0;
}
.page-type-search .page > .row > .col-sm-12 {
    padding: 0;
}

select>option {
    padding-top: 4px;
    padding-bottom: 4px;
    background-color: #fff;
}

// Breadcrumbs
#breadcrumbs-wrapper {
    background-color: #f7f8f9;
    padding: 20px 0 15px;
    margin-top: 15px;
    margin-bottom: 36px;
}

.page-type-page_contact_form #breadcrumbs-wrapper {

    margin-top: -6px;
}

.breadcrumbs {
    margin-bottom: 0;
    & .breadcrumb {
        display: inline-block;
        font-size: fontSize("base");
        padding-right: 10px;
        &::before {
            content: ">";
            color: #b4b4b4;
            position: relative;
            top: 0px;
            margin: 0px 12px 0 3px;
        }
        &:first-child::before {
            display: none;
        }
        & a:hover {
            border-bottom: 1px solid stencilColor('breadcrumb-label-color-hover');
        }
        & .breadcrumb-label {
            color: stencilColor('breadcrumb-label-color');
            &:hover {
                color: stencilColor('breadcrumb-label-color-hover');
            }
        }

        &.is-active {
            & .breadcrumb-label {
                color: stencilColor('breadcrumb-label-color-active');
            }
        }
    }

    @media(min-width: 1025px) {
        &.is-preNextProduct {
            padding-right: 60px;
        }
    }
}

.category-heading {
    clear: both;
    display: flex;
    justify-content: space-between;
    align-items: center;
    & .page-heading {
        display: inline-block;
        padding: 2px 0 20px;
        border: 0;
        margin-bottom: 3px;
        @media(max-width: 767px){
            font-weight: 600;
        }
        // @media (min-width: 768px) and (max-width: 991px) {
        //     padding-top: 10px;
        // }
    }
    @media (max-width: 991px) {
        flex-wrap: wrap;
    }
}

.page-type-createaccount .page-heading {
    margin: 0 0 27px;
    border-bottom: 1px solid #eeeeee;
    padding-bottom: 35px;
}

.page-heading {
    font-family: $fontFamily-headings;
    font-size: 24px;
    font-weight: 700;
    text-align: left;
    text-transform: capitalize;
    padding: 0 0 20px;
    margin-bottom: 3px;
}

.side-module-heading {
    margin-top: 22px;
    padding-bottom: 22px;
    margin-bottom: 0;
    font-size: 15px;
    text-transform: capitalize;
    letter-spacing: 0.05em;
}




/*Account Created Heading Title Spacing - Fixed*/

[data-page-no-breadcrumb] .page-heading {
    margin-top: 27px
}

.category-image {
    padding-bottom: 36px;
    margin-top: 13px;
    @media (max-width: 767px) {
        img {
            width: 100%;
        }
    }
}

.category-description {
    padding-bottom: 15px;
    border-bottom: 1px solid #e5e5e5;
    margin-bottom: 20px;
}

//View Mode Buttons (GRID/LIST)
.view-mode-btn {
    position: relative;
    display: inline-flex;
    align-items: center;
    width: 150px;
    vertical-align: top;
    
    @media (max-width: 490px) {
        margin-right: -6px;
    }
    & .btn-group {
        display: flex;
    }
    & label {
        font-weight: 600;
        font-family: $fontFamily-headings;
        font-size: fontSize("base");
        white-space: nowrap;
        width: 65px;
        letter-spacing: $headings-letter-spacing;
        padding-right: 12px;
    }
}


.view-mode-btn a,
.view-mode-btn a:hover,
.view-mode-btn a:active,
.view-mode-btn a:focus {
    font-size: 11px;
    font-weight: stencilFontWeight('headings-font');
    position: relative;
    display: flex;
    align-items: center;
    justify-content: flex-start;
    width: 40px;
    height: 40px;
    margin-right: 0;
    margin-left: 0;
    text-decoration: none;
    padding: 5px;
    border: 1px solid #e5e5e5;
}

.view-mode-btn a>div {
    width: 30px;
}

.view-mode-btn a>span {
    font-family: $fontFamily-headings;
    font-size: 11px;
    font-weight: stencilFontWeight('headings-font');
    text-transform: uppercase;
    letter-spacing: $headings-letter-spacing;
}

.view-mode-btn a.current-view {
    cursor: default;
}

.view-mode-btn a .icon-bar {
    position: relative;
    top: -1px;
    width: 14px;
    height: 4px;
    margin: 1px auto 0 auto;
    transition: all .15s ease;
    background: #e5e5e5;
    opacity: 1;
    filter: alpha(opacity=100);
}

.view-mode-btn a#grid-view {
    border-bottom: 1px solid #dedede;
    margin-right: -1px;
}

.view-mode-btn a#grid-view .icon-bar {
    width: 4px;
}

.view-mode-btn a#grid-view .icon-bar:before,
.view-mode-btn a#grid-view .icon-bar:after {
    position: absolute;
    display: block;
    width: 4px;
    height: 4px;
    content: '';
    transition: all .15s ease;
    background: #e5e5e5;
}

.view-mode-btn a#grid-view .icon-bar:before {
    left: -5px;
}

.view-mode-btn a#grid-view .icon-bar:after {
    right: -5px;
}

.view-mode-btn a.current-view .icon-bar {
    background: #232323;
}

.view-mode-btn a#grid-view.current-view .icon-bar:before,
.view-mode-btn a#grid-view.current-view .icon-bar:after {
    background: #232323;
}

.actionBar[data-sort-by] {
    text-align: right;
    margin: 0;
    display: inline-block;
    margin-left: 40px;
    & fieldset {
        float: none;
    }
    & .form-field {
        margin: 0;
    }
    & label {
        display: inline-block;
        font-size: fontSize("base");
        font-weight: 600;
        text-transform: uppercase;
        font-family: $fontFamily-headings;
        letter-spacing: $headings-letter-spacing;
        position: relative;
        top: 0;
        margin-right: 11px;
    }
    & select {
        display: inline-block;
        max-width: 180px;
        height: 40px;
        @media (max-width: 767px) {
            max-width: 160px;
        }
        @media (max-width: 600px) {
            max-width: 180px;
        }
        & option {
            padding: 5px 10px;
        }
    }
    // @media (min-width: 768px) and (max-width: 991px) {
    //     float: none;
    //     width: 100%;
    //     clear: both;
    //     padding-top: 10px;
    //     margin-left: 0;
    //     padding-right: 6px;
    // }
}

//Show More button
.showMoreProduct {
    display: inline-block;
    @media(min-width: 991px){
        display: flex;
        justify-content: center;
    }
    @media (max-width: 400px) {
        width: 100%;
        margin-bottom: 14px;
    }
}

.showMoreProduct a.btn {
    min-width: 300px;
    height: 42px;
    font-size: 13px;
    letter-spacing: 0.1em;
    line-height: 22px;
    @media(max-width: 810px){
        min-width: 200px;
    }
    @media (max-width: 400px) {
        width: 100%;
        text-align: center;
     }
}

.category-products {
    .showMoreProduct {
        display: block;
    }
    .showMoreProduct a.btn {
        min-width: 300px;
        margin-top: 25px;
        margin-bottom: 80px;
        height: 42px;
        font-size: 13px;
        letter-spacing: .1em;
        line-height: 22px;
        @media (max-width: 767px) {
            margin-top: 15px;
            margin-bottom: 55px;
        }
         @media (max-width: 380px) {
            margin-bottom: 40px;
         }
    }
}

// Home Top Promotions
#home-top-promotions {
    margin-top: -30px;
    padding: 0;
    & .promotion-wrap {
        display: flex;
        flex-wrap: wrap;
        margin-left: -15px;
        margin-right: -15px;
        & .promotion-item {
            width: 33.33%;
            text-align: center;
            padding: 0px 15px 15px;
            @media (max-width: 991px) {
                width: 100%;
            }
            &>a {
                padding: 10px 0;
                background: #3d3d3d;
                display: flex;
                flex-wrap: nowrap;
                justify-content: center;
                align-items: center;
                font-family: $fontFamily-headings;
                font-weight: $fontWeight-bold;
                text-transform: uppercase;
                color: #fff;
                font-size: 11px;
                letter-spacing: $headings-letter-spacing;
                line-height: 1.8em;
                min-height: 40px;
            }
            &.first>a {
                color: stencilColor('home_top_promotion_1_title_color');
                background: stencilColor('home_top_promotion_1_bgcolor');
            }
            &.second>a {
                color: stencilColor('home_top_promotion_2_title_color');
                background: stencilColor('home_top_promotion_2_bgcolor');
            }
            &.third>a {
                color: stencilColor('home_top_promotion_3_title_color');
                background: stencilColor('home_top_promotion_3_bgcolor');
            }
        }
    }
    @media (max-width: 991px) {
        margin-top: 0;
    }
}

// Home Top Custom Block
#homeTopCustomBlock {
    & .banner-item {
        padding: 0 15px 30px;
        position: relative;

        .banner-item-img {
            position: relative;
        }

        img{
            width: 100%;
        }
        @media (max-width: 480px) {
            width: 100%;
            margin-left: auto;
            margin-right: auto;
        }

        &.wide {
            width: 66.67%;
            @media (max-width: 480px) {
                width: 100%;
                margin-left: auto;
                margin-right: auto;
            }
        }
        &:hover .hover_collection {
            opacity: 1;
        }
        .collection_desc {
            letter-spacing: 0.05em;
            line-height: 24px;
            height: 90%;
            color: #ffffff;
            overflow: hidden;
            font-weight: 500;

            @media(min-width: 768px) {
                padding-top: 30px;
            }

            @media(min-width: 1200px) {
                padding-top: 50px;
            }
        }
        .hover_collection:after {
            top: 10px;
            right: 10px;
            bottom: 10px;
            left: 10px;
            border-right: 1px solid #fff;
            border-left: 1px solid #fff;
            -webkit-transform: scale(1, 0);
            transform: scale(1, 0);
            -webkit-transform-origin: 100% 0;
            transform-origin: 100% 0;
        }
        .hover_collection:before {
            top: 10px;
            right: 10px;
            bottom: 10px;
            left: 10px;
            border-top: 1px solid #fff;
            border-bottom: 1px solid #fff;
            -webkit-transform: scale(0, 1);
            transform: scale(0, 1);
            -webkit-transform-origin: 0 0;
            transform-origin: 0 0;
        }
        .hover_collection img,
        .hover_collection:before,
        .hover_collection:after {
            -webkit-transition: opacity 0.35s, -webkit-transform 0.35s;
            transition: opacity 0.35s, transform 0.35s;
        }
        .hover_collection:before,
        .hover_collection:after {
            position: absolute;
            content: '';
            opacity: 0;
        }
        .hover_collection:hover:before,
        .hover_collection:hover:after {
            opacity: 1;
            -webkit-transform: scale(1);
            transform: scale(1);
        }
        .hover_collection {
            position: absolute;
            top: 0;
            height: 100%;
            left: 0px;
            width: 100%;
            background: rgba(0, 0, 0, 0.46);
            z-index: 99;
            padding: 30px;
            opacity: 0;
            -webkit-transition: all 0.3s ease-in-out;
            -moz-transition: all 0.3s ease-in-out;
            -o-transition: all 0.3s ease-in-out;
            transition: all 0.3s ease-in-out;
        }
        .collection-title {
            border-bottom: 3px solid #eef2f2;
            margin-top: 20px;
            .collection_type {
                font-weight: 600;
                text-transform: uppercase;
                padding-bottom: 10px;
                letter-spacing: 1.3px;
                font-size: fontSize("base");
                color: #676767;
            }
            .text-title {
                font-weight: 700;
                margin-top: 7px;
                margin-bottom: 23px;
                letter-spacing: 0.7px;
                font-size: 20px;
                color: stencilColor('color-textHeading');
            }
            .collection-btn {
                text-transform: uppercase;
                font-weight: 700;
                margin-bottom: 17px;
                display: inline-block;
                text-decoration: none;
                position: relative;
                letter-spacing: 1px;
                line-height: 1.3;
                font-size: 13px;
                color: stencilColor('color-textHeading');
                border-bottom: 1px dotted transparent;
                &:hover{
                    border-bottom: 1px dotted;
                }
                &:hover:before {
                    right: 0px;
                }
                &:before {
                    content: "";
                    position: absolute;
                    z-index: -1;
                    left: 0;
                    right: 100%;
                    bottom: 0;
                    height: 2px;
                    background: #000000;
                    -webkit-transition-property: right;
                    transition-property: right;
                    -webkit-transition-duration: 0.3s;
                    transition-duration: 0.3s;
                    -webkit-transition-timing-function: ease-out;
                    transition-timing-function: ease-out;
                }
                .fa {
                    font-weight: 700;
                    padding-left: 8px;
                    font-size: 14px;
                }
            }
        }
        .collection_total {
            color: #ffffff;
            font-size: 12px;
            font-weight: 600;
            letter-spacing: 0.1em;
            text-transform: uppercase;
            padding-top: 5px;
        }
    }
}

