#InstagramGallery .feed-item::before {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    content: '';
    -webkit-transition: all .3s ease 0s;
    -moz-transition: all .3s ease 0s;
    -ms-transition: all .3s ease 0s;
    -o-transition: all .3s ease 0s;
    transition: all .3s ease 0s;
    opacity: 0;
    filter: alpha(opacity=0);
    background: #241d1c;
}

#InstagramGallery .feed-item:hover::before {
    -webkit-transition: all .3s ease 0s;
    -moz-transition: all .3s ease 0s;
    -ms-transition: all .3s ease 0s;
    -o-transition: all .3s ease 0s;
    transition: all .3s ease 0s;
    opacity: 0.65;
    filter: alpha(opacity=65);
}

#InstagramGallery .feed-item::after {
    content: '';
    position: absolute;
    top: 0px;
    bottom: 0px;
    left: 0px;
    right: 0px;
    border: 1px solid #ffffff;
    -webkit-transition: all .3s ease 0s;
    -moz-transition: all .3s ease 0s;
    -ms-transition: all .3s ease 0s;
    -o-transition: all .3s ease 0s;
    transition: all .3s ease 0s;
    opacity: 0;
    filter: alpha(opacity=0);
}

#InstagramGallery .feed-item:hover::after {
    top: 8px;
    bottom: 8px;
    left: 8px;
    right: 8px;
    -webkit-transition: all .3s ease 0s;
    -moz-transition: all .3s ease 0s;
    -ms-transition: all .3s ease 0s;
    -o-transition: all .3s ease 0s;
    transition: all .3s ease 0s;
    opacity: 0.6;
    filter: alpha(opacity=60);
}

#InstagramGallery .feed-item .feed-action {
    position: absolute;
    top: 50%;
    right: 0;
    left: 0;
    visibility: hidden;
    -webkit-transition: all .3s ease 0s;
    -moz-transition: all .3s ease 0s;
    -ms-transition: all .3s ease 0s;
    -o-transition: all .3s ease 0s;
    transition: all .3s ease 0s;
    -webkit-transform: translateY(-50%);
    -moz-transform: translateY(-50%);
    -ms-transform: translateY(-50%);
    -o-transform: translateY(-50%);
    transform: translateY(-50%);
    opacity: 0;
    filter: alpha(opacity=0);
    z-index: 1;
}

#InstagramGallery .feed-item:hover .feed-action {
    visibility: visible;
    -webkit-transition: all .3s ease 0s;
    -moz-transition: all .3s ease 0s;
    -ms-transition: all .3s ease 0s;
    -o-transition: all .3s ease 0s;
    transition: all .3s ease 0s;
    opacity: 1;
    filter: alpha(opacity=100);
}

#InstagramGallery .feed-item .feed-action .btn {
    font-size: 14px;
    width: 36px;
    margin: 3px;
    padding-right: 0;
    padding-left: 0;
    text-align: center;
    background: #ffffff;
    border-color: #ffffff;
    color: #000;
