components/halothemes/halo-compare-color:
{{#if theme_settings.halo_compare_colors}}
    {{#if product.options.length}}
        {{#each product.options}}
            {{#if type '===' 'Configurable_PickList_Swatch'}}
                <div class="halo-compareColors">
                    <a class="halo-compareColors-link link link-hover" href="#" data-reveal-id="compare-color-popup" aria-label="Button Compare Colors">
                        <span class="icon-compare-color"></span>
                        <span class="halo-compareColors-title">{{lang 'products.compare_colors'}}</span>
                    </a>
                    <div id="compare-color-popup" class="modal modal--medium" data-reveal>
                        <div class="modal-content">
                            <div class="modal-header modal-header3">
                                <h2 class="modal-header-title">{{lang 'products.compare_colors_title'}}</h2>
                                <a href="#" class="modal-close" aria-label="Close" role="button">
                                    <span aria-hidden="true"><svg><use xlink:href="#icon-close"></use></svg></span>
                                </a>
                            </div>
                            <div class="modal-body modal-body2">
                                <div class="halo-compareColors-swatch" data-compare-color>
                                    {{#each this.values}}
                                        <label class="form-option form-option-swatch" data-product-swatch-value="{{id}}">
                                            {{#if image}}
                                                <span class='form-option-variant form-option-variant--pattern' title="{{this.label}}" style="background-image: url('{{getImage image "24x24"}}');"></span>
                                            {{/if}}
                                            {{#if data.[2]}}
                                                <span class='form-option-variant form-option-variant--color3' title="{{this.label}}">
                                                    <span style="background-color: #{{data.[0]}}"></span>
                                                    <span style="background-color: #{{data.[1]}}"></span>
                                                    <span style="background-color: #{{data.[2]}}"></span>
                                                </span>
                                            {{else}}
                                                {{#if data.[1]}}
                                                    <span class='form-option-variant form-option-variant--color2' title="{{this.label}}">
                                                        <span style="background-color: #{{data.[0]}}"></span>
                                                        <span style="background-color: #{{data.[1]}}"></span>
                                                    </span>
                                                {{else}}
                                                    {{#if data.[0]}}
                                                        <span class='form-option-variant form-option-variant--color' title="{{this.label}}" style="background-color: #{{data.[0]}}"></span>
                                                    {{/if}}
                                                {{/if}}
                                            {{/if}}
                                        </label>
                                    {{/each}}
                                </div>
                                <div id="color-swatch-image" class="halo-compareColors-image"></div>
                            </div>
                        </div>
                    </div>
                </div>
            {{/if}}
        {{/each}}
    {{/if}}
{{/if}}

components/products/price:
{{#or has_options product.options.length}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_with_tax}}
                <span data-product-rrp-with-tax class="price price--rrp">{{price.rrp_with_tax.formatted}}</span>
            {{/if}}
            {{#if page_type '!==' 'product'}}
                {{#if theme_settings.price_as_low_as}}
                    <span translate>As low as </span>
                {{/if}}
            {{/if}}
            <span data-product-price-with-tax class="price price--withTax">{{price.with_tax.formatted}}</span>
            {{#if price.without_tax}}
                {{lang 'products.price_with_tax' tax_label=price.tax_label}}
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}"  {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_without_tax}}
                <span data-product-rrp-without-tax class="price price--rrp">{{price.rrp_without_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="price" content="{{price.without_tax.value}}">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="availability" content="{{product.availability}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            {{/if}}
            {{#if page_type '!==' 'product'}}
                {{#if theme_settings.price_as_low_as}}
                    <span translate>As low as </span>
                {{/if}}
            {{/if}}
            <span data-product-price-without-tax class="price price--withoutTax">{{price.without_tax.formatted}}</span>
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
        {{#if price.saved}}
            <div class="price-section price-section--saving">
                <span class="price">
                    {{lang 'products.you_save' amount=price.saved.formatted}}
                </span>
            </div>
        {{/if}}
    {{/if}}
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_with_tax}}
                <span data-product-rrp-with-tax class="price price--rrp">{{price.rrp_with_tax.formatted}}</span>
            {{/if}}
            <span data-product-price-with-tax class="price price--withTax">{{price.with_tax.formatted}}</span>
            {{#if price.without_tax}}
                {{lang 'products.price_with_tax' tax_label=price.tax_label}}
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}"  {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_without_tax}}
                <span data-product-rrp-without-tax class="price price--rrp">{{price.rrp_without_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="price" content="{{price.without_tax.value}}">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
            {{/if}}
            <span data-product-price-without-tax class="price price--withoutTax">{{price.without_tax.formatted}}</span>
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
        {{#if price.saved}}
            <div class="price-section price-section--saving">
                <span class="price">
                    {{lang 'products.you_save' amount=price.saved.formatted}}
                </span>
            </div>
        {{/if}}
    {{/if}}
{{/or}}
