components/halothemes/home-banners:
{{#if banner '==' 'home-top-banners'}}
    {{> components/halothemes/home-layout-banner find="homeTopCustomBlock"}}
{{/if}}

{{#if banner '==' 'home-bottom-custom-block'}}
    {{> components/halothemes/home-layout-banner find="homeBottomCustomBlock"}}
{{/if}}

{{#if banner '==' 'home-brand-slider'}}
    {{> components/halothemes/home-layout-banner find="homeBrands"}}
{{/if}}

{{#if banner '==' 'custom-cms-block'}}
    {{> components/halothemes/home-layout-banner find="customCMSBlock"}}
{{/if}}

components/halothemes/home-layout-banner:
{{!--
Arguments:
    - find (string)
--}}
{{#if banners.top}}
    {{#each banners.top}}
        {{#if (occurrences this ../find) '>' 0}}
            {{{this}}}
        {{/if}}
    {{/each}}
{{/if}}
{{#if banners.bottom}}
    {{#each banners.bottom}}
        {{#if (occurrences this ../find) '>' 0}}
            {{{this}}}
        {{/if}}
    {{/each}}
{{/if}}
